import React from 'react';

const HomeCarousel = ({ section }) => {
  const sectionNumber = section + 1;
  console.log(`Cargando sección ${sectionNumber}`);

  const images = Array.from({ length: 20 }, (_, index) => {
    const imageNumber = index + 1;
    const imagePath = `../assets/imgs/home_image_section${sectionNumber}/section${sectionNumber}_image${imageNumber}.jpg`;

    try {
      const img = require(`../assets/imgs/home_image_section${sectionNumber}/section${sectionNumber}_image${imageNumber}.jpg`).default;
      console.log(`Imagen cargada: ${imagePath}`);
      return img;
    } catch (error) {
      console.error(`ERROR cargando imagen: ${imagePath}`, error);
      return null;
    }
  }).filter(Boolean);

  console.log(`Imágenes encontradas para sección ${sectionNumber}:`, images.length);

  return (
    <div className="home__carrousel__img__rows">
      {images.map((img, index) => (
        <div
          key={index}
          className="image-part-container"
          style={{
            width: `${100 / images.length}%`,
            height: '100%',
            overflow: 'hidden',
            flexShrink: 0,
            border: '1px solid red' // Borde temporal para debug
          }}
        >
          <img
            src={img}
            alt={`Sección ${sectionNumber} - Parte ${index + 1}`}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              objectPosition: 'center',
              border: '1px solid blue' // Borde temporal para debug
            }}
          />
        </div>
      ))}
    </div>
  );
};

export default HomeCarousel;
