import React, { useState, useEffect } from 'react';
import GrainOverlay from './components/GrainOverlay';
import Preloader from './components/Preloader';
import Home from './components/Home';
import About from './components/About';
import Projects from './components/Projects';
import Contact from './components/Contact';
import Cursor from './components/Cursor';
import Nav from './components/Nav';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [videoLoaded, setVideoLoaded] = useState(false);
  const [preloaderClosing, setPreloaderClosing] = useState(false);
  const [preloaderHidden, setPreloaderHidden] = useState(false);

  useEffect(() => {
    if (videoLoaded) {
      setPreloaderClosing(true);
      const timeout = setTimeout(() => setPreloaderHidden(true), 1000);
      return () => clearTimeout(timeout);
    }
  }, [videoLoaded]);

  const handleVideoLoad = () => {
    setVideoLoaded(true);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      default:
        return null;
    }
  };

  return (
    <>
      {!preloaderHidden && (
        <Preloader closing={preloaderClosing} />
      )}

      <div className="app">
        <GrainOverlay />
        <video
          className={`background-video ${videoLoaded ? 'visible' : 'hidden'}`}
          autoPlay
          loop
          muted
          playsInline
          onCanPlayThrough={handleVideoLoad}
        >
          <source src={background} type="video/mp4" />
        </video>
        <Nav setCurrentPage={setCurrentPage} />
        <div className="content"><Cursor />{renderPage()}</div>
      </div>
    </>
  );
};

export default App;
