import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import '../assets/styles/App.css';
import profile from '../assets/imgs/viper.jpg';
import SectionFooter from './SectionFooter';

const Home = () => {
  const [currentSection, setCurrentSection] = useState(1);
  const [scrollDirection, setScrollDirection] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const [exitAnimationDone, setExitAnimationDone] = useState(false); // Para manejar el estado de la animación de salida

  const handleSectionChangeAlert = (origin, destination) => {
    const direction = origin < destination ? 'down' : 'up';
    setScrollDirection(direction); // Actualizamos la dirección
  };

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    document.body.style.touchAction = 'none';

    const handleScroll = (e) => {
      if (isAnimating || exitAnimationDone) return; // No hacemos nada si ya estamos animando o la animación de salida ya terminó

      if (e.deltaY > 0 && currentSection < 3) {
        setIsAnimating(true);
        setExitAnimationDone(false); // Aseguramos que la animación de salida no haya terminado aún
        setCurrentSection((prev) => {
          const newSection = prev + 1;
          handleSectionChangeAlert(prev, newSection); // Llamamos a la nueva función
          return newSection;
        });
      } else if (e.deltaY < 0 && currentSection > 1) {
        setIsAnimating(true);
        setExitAnimationDone(false); // Aseguramos que la animación de salida no haya terminado aún
        setCurrentSection((prev) => {
          const newSection = prev - 1;
          handleSectionChangeAlert(prev, newSection); // Llamamos a la nueva función
          return newSection;
        });
      }
    };

    window.addEventListener('wheel', handleScroll);

    return () => {
      document.body.style.overflow = 'auto';
      document.body.style.touchAction = 'auto';
      window.removeEventListener('wheel', handleScroll);
    };
  }, [currentSection, isAnimating, exitAnimationDone]);

  const handleFooterNavigation = (newSection) => {
    if (newSection !== currentSection) {
      setIsAnimating(true);
      setExitAnimationDone(false); // Aseguramos que la animación de salida no haya terminado aún
      handleSectionChangeAlert(currentSection, newSection); // Llamamos a la nueva función con dirección calculada
      setCurrentSection(newSection);
    }
  };

  const renderSection = () => {
    switch (currentSection) {
      case 1:
        return (
          <>
            {/* Animación de salida antes de cambiar a la siguiente sección */}
            <motion.div
              key={`exit-1`}
              className="home__section1"
              initial={{ x: 0 }}
              animate={{ x: scrollDirection === 'down' ? '-100%' : '100%' }}
              transition={{ type: 'spring', stiffness: 300 }}
              onAnimationComplete={() => setExitAnimationDone(true)} // Después de la animación de salida, cambiamos el estado
            >
              <div className="section">
                <div className="profile">
                  <img src={profile} alt="Profile" />
                </div>
                <div className="home__section1__message">
                  <p><b>hey, im dave</b></p>
                  <p>
                    i'm a developer based in <b>Barcelona</b>. I create things like&nbsp;
                    <span style={{ color: '#e96f7c' }}>sites & apps</span> and delve into&nbsp;
                    <span style={{ color: '#e96f7c' }}>hacking & malware</span> and whatever else.
                    The page is still under maintenance, so feel free to look around if you want..
                  </p>
                </div>
              </div>
            </motion.div>

            {/* Animación de entrada para la nueva sección */}
            {exitAnimationDone && (
              <motion.div
                key={2}
                className="home__section2"
                initial={{ x: scrollDirection === 'down' ? '100%' : '-100%' }}
                animate={{ x: 0 }}
                exit={{ x: scrollDirection === 'down' ? '-100%' : '100%' }}
                transition={{ type: 'spring', stiffness: 300 }}
                onAnimationComplete={() => setIsAnimating(false)} // Reset animation flag
              >
                <h1 style={{ color: 'white' }}>Special Abilities</h1>
                <ul>
                  <li>Desarrollo Web: Creación de sitios y aplicaciones web dinámicas y responsivas.</li>
                  <li>Programación de Software: Desarrollo de aplicaciones personalizadas para diversas necesidades.</li>
                  <li>Ciberseguridad: Especializado en malware pentesting y prácticas seguras de red.</li>
                  <li>Redes Informáticas: Configuración y optimización de infraestructuras de red.</li>
                </ul>
                <p>
                  <b>PD:</b> if you want to know more about my skills, you can contact me through my social networks.
                </p>
              </motion.div>
            )}
          </>
        );

      case 2:
        return (
          <>
            {/* Animación de salida antes de cambiar a la siguiente sección */}
            <motion.div
              key={`exit-2`}
              className="home__section2"
              initial={{ x: 0 }}
              animate={{ x: scrollDirection === 'down' ? '-100%' : '100%' }}
              transition={{ type: 'spring', stiffness: 300 }}
              onAnimationComplete={() => setExitAnimationDone(true)} // Después de la animación de salida, cambiamos el estado
            >
              <h1 style={{ color: 'white' }}>Special Abilities</h1>
              <ul>
                <li>Desarrollo Web: Creación de sitios y aplicaciones web dinámicas y responsivas.</li>
                <li>Programación de Software: Desarrollo de aplicaciones personalizadas para diversas necesidades.</li>
                <li>Ciberseguridad: Especializado en malware pentesting y prácticas seguras de red.</li>
                <li>Redes Informáticas: Configuración y optimización de infraestructuras de red.</li>
              </ul>
              <p>
                <b>PD:</b> if you want to know more about my skills, you can contact me through my social networks.
              </p>
            </motion.div>

            {/* Animación de entrada para la nueva sección */}
            {exitAnimationDone && (
              <motion.div
                key={3}
                className="home__section3"
                initial={{ x: scrollDirection === 'down' ? '100%' : '-100%' }}
                animate={{ x: 0 }}
                exit={{ x: scrollDirection === 'down' ? '-100%' : '100%' }}
                transition={{ type: 'spring', stiffness: 300 }}
                onAnimationComplete={() => setIsAnimating(false)} // Reset animation flag
              >
                <h1 style={{ color: 'white' }}>Some of my stuff and side projects:</h1>
                <ul>
                  <li>Proyect 1: <a href="">Link</a></li>
                  <li>Proyect 2: <a href="">Link</a></li>
                  <li>Proyect 3: <a href="">Link</a></li>
                  <li>Proyect 4: <a href="">Link</a></li>
                </ul>
              </motion.div>
            )}
          </>
        );

      default:
        return null;
    }
  };

  return (
    <div className="content">
      <div>
        {renderSection()}
        <SectionFooter
          currentSection={currentSection}
          setCurrentSection={handleFooterNavigation} // Usamos handleFooterNavigation para asegurar que la dirección sea correcta
        />
      </div>
    </div>
  );
};

export default Home;
