import React, { useState, useEffect } from 'react';
import GrainOverlay from './GrainOverlay';
import '../assets/styles/App.css';
import glitch from '..assets/imgs/glitch.gif';

const Preloader = () => {
  const [closing, setClosing] = useState(false);
  const [showGif, setShowGif] = useState(false); // Estado para mostrar el GIF

  useEffect(() => {
    // Esperar 4 segundos antes de hacer el blur y cambiar la opacidad
    const timeout = setTimeout(() => {
      setClosing(true); // Cambia el estado para aplicar la animación de salida después de 4 segundos
    }, 4000);

    // Mostrar el GIF al final de la animación
    const gifTimeout = setTimeout(() => {
      setShowGif(true); // Cambia el estado para mostrar el GIF
    }, 5000); // Un segundo después del cierre (opcional)

    // Establece el comportamiento al cerrar o recargar la página
    const handleBeforeUnload = () => {
      setClosing(true); // Cambia el estado si la página se va a cerrar
    };

    // Detecta cuando la pestaña se va a cerrar o recargar
    window.addEventListener('beforeunload', handleBeforeUnload);

    // Limpiar los timeouts cuando el componente se desmonta
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
      clearTimeout(timeout);
      clearTimeout(gifTimeout);
    };
  }, []);

  return (
    <>
      {showGif ? (
        // Mostrar el GIF al final
        <div className="GifContainer">
          glith
        </div>
      ) : (
        // Preloader con GrainOverlay siempre visible
        <div className={`Preloader__Content ${closing ? 'closing' : ''}`}>
          <GrainOverlay />
          <div className="Preloader__Container">
            <div className="Preloader__Text">
              STANDBY..
              <span className="blinking-dot">.</span>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Preloader;
