import React from 'react';

const importSectionImages = (sectionNumber) => {
  try {
    const context = require.context(
      `../assets/imgs/home_image_section${sectionNumber}`,
      false,
      /section\d+_image_\d+\.jpg$/ // Regex corregido
    );
    return context.keys().map(context);
  } catch (error) {
    console.error(`Error loading images for section ${sectionNumber}:`, error);
    return [];
  }
};

const HomeCarousel = ({ section }) => {
  const sectionNumber = section + 1;
  const images = importSectionImages(sectionNumber);

  return (
    <div className="home__carrousel__img__rows">
      {images.map((img, index) => (
        <div
          key={index}
          className="image-part-container"
          style={{
            width: `${100 / images.length}%`,
            height: '100%',
            overflow: 'hidden',
            flexShrink: 0,
          }}
        >
          <img
            src={img.default || img}
            alt={`Sección ${sectionNumber} - Parte ${index + 1}`}
            className="images-rows"
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover'
            }}
          />
        </div>
      ))}
    </div>
  );
};

export default HomeCarousel;
