import React, { useState } from 'react';
import '../assets/styles/App.css';
import profile from '../assets/imgs/viper.jpg';

const About = () => {
  const [isVisible, setIsVisible] = useState(false);

  const handleScroll = () => {
    const section = document.getElementById('target-section');
    section.scrollIntoView({ behavior: 'smooth' });
    setTimeout(() => {
      setIsVisible(true);
    }, 500); // Retardo para que la animación se vea tras el scroll
  };

  return (
    <div className="content">
      <div className="about-1">
        <div className="about-text">
          <div className="about-bio">
            <p>I’ve studied cybersecurity and am currently diving into computer science software engineering. I’ve dabbled in a lot, like  
              pentesting, OSINT, malware dev, software dev and websites. I just kind of do it all. 
              Nothing too crazy, but there's always more to learn, though.</p>
          </div>
          <div className="about-bio-list">
            <ul>
              <li>Location: Barcelona, Spain</li>
              <li>Expertise: Software Development & Cybersecurity</li>
              <li>Hobbies: Cook</li>
              <li>Experience: <span style={{ fontFamily: 'arial', fontSize: '15px' }}>5</span>+ Years</li>
            </ul>
          </div>
        </div>

        <div className="about-bio-img">
          <img src={profile} alt="Profile" />
        </div>
      </div>

      {/* Botón de scroll */}
      <div className="scroll-button">
        <button onClick={handleScroll}>Scroll Down</button>
      </div>

      {/* Sección oculta que aparecerá después del scroll */}
      <div id="target-section" className={`hidden-section ${isVisible ? 'visible' : ''}`}>
        <p>This is the content of the lower section that appears after you scroll down!</p>
      </div>
    </div>
  );
};

export default About;
