import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Testing from "./components/Testing";
import Navbar from './components/Header';
import Footer from './components/SectionFooter'; // Importa el Footer
import GrainOverlay from './components/GrainOverlay';
import Spinner from './components/Preloader'; // Importa el Spinner
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [showWelcome, setShowWelcome] = useState(true); // Controla la animación de bienvenida
  const [videoLoaded, setVideoLoaded] = useState(false); // Controla si el video de fondo está cargado
  const [pageLoaded, setPageLoaded] = useState(false); // Controla si la página está completamente cargada

  document.ontouchmove = function (event) {
    event.preventDefault();
  };

  useEffect(() => {
    const welcomeTimeout = setTimeout(() => {
      setShowWelcome(false);
    }, 5000);

    // Habilitar la animación después de 5 segundos (cuando el spinner se desvanece)
    const pageTimeout = setTimeout(() => {
      setPageLoaded(true);
    }, 5000);

    return () => {
      clearTimeout(welcomeTimeout);
      clearTimeout(pageTimeout);
    };
  }, []);

  const handleVideoLoad = () => {
    setVideoLoaded(true); // Cambia el estado cuando el video esté listo
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      case 'testing':
        return <Testing />;
      default:
        return null;
    }
  };

  return (
    <>
      {/* Pantalla de bienvenida */}
      {showWelcome ? (
        <Spinner />
      ) : (
        <div>
          <GrainOverlay />

          {/* Aplica la clase fade-in-blur cuando la página haya cargado */}
          <Navbar setCurrentPage={setCurrentPage} className={pageLoaded ? 'fade-in-blur' : ''} />

          <div className={`home ${pageLoaded ? 'fade-in-blur' : ''}`}>
            <video
              className={`videoTag ${videoLoaded ? 'visible' : 'hidden'}`}
              autoPlay
              loop
              muted
              playsInline
              onLoadedData={handleVideoLoad}
            >
              <source src={background} type="video/mp4" />
            </video>
            {renderPage()}
          </div>

          <Footer />
        </div>
      )}
    </>
  );
};

export default App;
