import React, { useRef, useEffect } from "react";
import * as THREE from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";

// CircleXZAnimation (versión esfera - ascenso contínuo)
// - Canvas ocupa toda la ventana
// - La circunferencia en XZ sigue la sección de una esfera de radio R = 50
// - La animación sube desde y=0 hasta y=100 de forma continua y, al llegar a y=100,
//   salta de nuevo a y=0 y vuelve a empezar (no hace ping-pong)
// - Velocidad reducida para un ascenso más lento

export default function CircleXZAnimation() {
  const mountRef = useRef(null);

  useEffect(() => {
    const mount = mountRef.current;
    if (!mount) return;

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x071026);

    const width = mount.clientWidth;
    const height = mount.clientHeight;

    const camera = new THREE.PerspectiveCamera(55, width / height, 0.1, 1000);
    camera.position.set(0, 140, 260);
    camera.lookAt(0, 50, 0);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    renderer.setPixelRatio(window.devicePixelRatio);
    mount.appendChild(renderer.domElement);

    // Luces
    const amb = new THREE.AmbientLight(0xffffff, 0.55);
    scene.add(amb);
    const dir = new THREE.DirectionalLight(0xffffff, 0.4);
    dir.position.set(50, 120, 80);
    scene.add(dir);

    // Grid para referencia en XZ
    const grid = new THREE.GridHelper(300, 30, 0x333333, 0x1a1a1a);
    scene.add(grid);

    // Parámetros de la "esfera"
    const R = 50; // radio de la esfera
    const Y_MIN = 0;
    const Y_MAX = 100;

    // Creamos una línea circular en el plano XZ (y = 0), actualizaremos sus vértices para escalarla
    const segments = 256;
    const positions = new Float32Array((segments + 1) * 3); // +1 para cerrar el bucle

    const geometry = new THREE.BufferGeometry();
    geometry.setAttribute("position", new THREE.BufferAttribute(positions, 3));
    geometry.setDrawRange(0, segments + 1);

    const material = new THREE.LineBasicMaterial({
      color: 0x7be495,
      transparent: true,
      opacity: 0.95,
    });

    const circleLine = new THREE.Line(geometry, material);
    scene.add(circleLine);

    // Controles para inspección
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.target.set(0, 50, 0);
    controls.update();

    // Reloj y variables de animacion
    const clock = new THREE.Clock();
    let y = 0;
    const speed = 6; // unidades Y por segundo (más lento)

    // Rellenar posiciones de la circunferencia para un radio dado
    function updateCircleGeometry(radius) {
      for (let i = 0; i <= segments; i++) {
        const t = (i / segments) * Math.PI * 2;
        const x = radius * Math.cos(t);
        const z = radius * Math.sin(t);
        const idx = i * 3;
        positions[idx] = x;
        positions[idx + 1] = 0; // Y local
        positions[idx + 2] = z;
      }
      geometry.attributes.position.needsUpdate = true;
    }

    // Función que devuelve radio de la sección horizontal de una esfera de radio R centrada en y=R
    function sphereRadiusAtY(yPos) {
      const val = R * R - (yPos - R) * (yPos - R);
      return val > 0 ? Math.sqrt(val) : 0;
    }

    // Inicializamos
    updateCircleGeometry(0);
    circleLine.position.y = 0;

    function animate() {
      const delta = clock.getDelta();

      // Subimos continuamente. Al llegar a Y_MAX, reiniciamos a Y_MIN para empezar otra vez
      y += speed * delta;
      if (y >= Y_MAX) {
        // Reinicio inmediato al empezar desde abajo (no hay descenso)
        y = Y_MIN;
      }

      const r = sphereRadiusAtY(y);
      updateCircleGeometry(r);
      circleLine.position.y = y;

      renderer.render(scene, camera);
      requestAnimationFrame(animate);
    }

    clock.start();
    animate();

    // Resize
    function handleResize() {
      const w = mount.clientWidth;
      const h = mount.clientHeight;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    }
    window.addEventListener("resize", handleResize);

    // Cleanup
    return () => {
      window.removeEventListener("resize", handleResize);
      mount.removeChild(renderer.domElement);
      geometry.dispose();
      material.dispose();
      renderer.dispose();
      controls.dispose();
    };
  }, []);

  return (
    <div ref={mountRef} style={{ width: "100vw", height: "100vh", position: "relative" }}>
      <div style={{ position: "absolute", left: 12, top: 12, padding: "6px 10px", background: "rgba(0,0,0,0.35)", color: "#e6fff0", borderRadius: 8, fontSize: 13 }}>
        Ascenso continuo: y desde 0 hasta 100 — reinicia desde abajo al llegar a la cima
      </div>
    </div>
  );
}
