import React, { useRef } from 'react';
import './ImageCarousel.css';

const ImageCarousel = ({ image, animate }) => {
  const overlayRef = useRef(null);
  const totalRows = 20;
  const animationDuration = 600;
  const delayIncrement = 50;

  const handleMouseMove = (e) => {
    if (overlayRef.current) {
      const rect = overlayRef.current.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      overlayRef.current.style.setProperty('--mouse-x', `${x}px`);
      overlayRef.current.style.setProperty('--mouse-y', `${y}px`);
    }
  };

  return (
    <div className="home__carrousel" onMouseMove={handleMouseMove}>
      <img 
        src={image} 
        alt="Carousel" 
        className="home__carrousel__img" 
      />

      <div 
        ref={overlayRef}
        className="carousel__overlay"
      />

      <div className="home__carrousel__img__rows">
        {Array.from({ length: totalRows }, (_, index) => (
          <div
            key={index}
            className="home__carrousel__img__row"
            style={{ '--delay': `${index * delayIncrement}ms` }}
          >
            <div
              className="home__carrousel__img__row__inner"
              style={animate ? {
                animation: `expandWidth ${animationDuration}ms cubic-bezier(0.4, 0, 0.2, 1) forwards`,
                animationDelay: `${index * delayIncrement}ms`
              } : {}}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageCarousel;
