// App.jsx
import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import NotFoundPage from "./components/NotFoundPage";
import Navbar from './components/Header';
import GrainOverlay from './components/GrainOverlay'; // Assuming you have GrainOverlay
import background from './assets/imgs/background.mp4'; // Import your video here

const App = () => {
  const getPageFromHash = () => {
    const hash = window.location.hash.substring(1);
    return hash || 'home';
  };

  const [currentPage, setCurrentPage] = useState(getPageFromHash());
  const [loading, setLoading] = useState(true); // for video loading

  useEffect(() => {
    const onHashChange = () => {
      setCurrentPage(getPageFromHash());
    };

    window.addEventListener('hashchange', onHashChange);

    return () => {
      window.removeEventListener('hashchange', onHashChange);
    };
  }, []);

  const handleSetPage = (page) => {
    setCurrentPage(page);
    window.location.hash = page; // Update the URL hash
  };

  const renderPage = () => {
    switch(currentPage){
      case 'home':
        return <Home loading={loading} />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      default:
        return <NotFoundPage />;
    }
  };

  const handleVideoLoad = () => {
    setLoading(false);
  };

  return (
    <>
      <Navbar setCurrentPage={handleSetPage} />

      {/* Background Video */}
      <video 
        className='videoTag' 
        autoPlay 
        loop 
        muted 
        playsInline 
        onLoadedData={handleVideoLoad}
        style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', objectFit: 'cover', zIndex: -1 }}
      >
        <source src={background} type='video/mp4' />
      </video>

      {/* Grain Overlay */}
      <GrainOverlay />

      {/* Render Current Page */}
      <div className={`content ${loading ? 'loading' : 'loaded'}`}>
        {renderPage()}
      </div>
    </>
  );
};

export default App;
