import React, { useEffect, useRef } from 'react';
import '../assets/styles/Nav.css';
import '../assets/styles/App.css';
import DecryptedText from './extra_functions/DecryptedText';
import barcode_logo from '../assets/imgs/barcode.svg';
import recycle_logo from '../assets/imgs/recycle_logo.svg';
import weee_symbol_logo from '../assets/imgs/WEEE_symbol_logo.svg';
import ce_logo from '../assets/imgs/CE_logo.svg';

const About = ({ setAboutSection }) => {
  const section1Ref = useRef(null);
  const section2Ref = useRef(null);
  const section3Ref = useRef(null);

  useEffect(() => {
    const sectionsContainer = document.querySelector('.about__sections__container');
    const handleScroll = () => {
      const scrollPosition = sectionsContainer.scrollTop;
      const windowHeight = window.innerHeight;
      if (scrollPosition < windowHeight * 0.5) {
        setAboutSection(1);
      } else if (scrollPosition < windowHeight * 1.5) {
        setAboutSection(2);
      } else {
        setAboutSection(3);
      }
    };
    sectionsContainer.addEventListener('scroll', handleScroll);
    return () => sectionsContainer.removeEventListener('scroll', handleScroll);
  }, [setAboutSection]);

  return (
    <div className="about__page__content">
      <div className="about__sections__container">

        <section className="about__section1" ref={section1Ref}>
          <div className="about__left1">
            <h1 className="about__section1__title">
              <DecryptedText
                text="VEQQAR"
                encryptedText="V3QQ42"
                iterations={10}
                speed={35}
                charset="0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ!@#$%&"
                className="decrypted-text"
                encryptedClassName="encrypted-text"
                parentClassName="decrypted-text-container"
              />
            </h1>
          </div>
        </section>

        {/* Sección 2 */}
        <section className="about__section2" ref={section2Ref}>
          <div className="about__left2">
            <h1 className="about__section2__title"></h1>
          </div>
          <div className="about__right2">
            <div className='about__section2__card__container'>
              <div className='about__section2__card__barcode'>
                <img 
                  src={barcode_logo} 
                  className='about__section2__barcode'
                />
              </div>
              <div className='about__section2__card__text'>
                <div className='about__section2__card__title'>
                  id: veqqar
                </div>
                <div className='about__section2__card__title'>
                  //
                </div>
                <div className='about__section2__card__title'>
                  year: <span style={{fontFamily: "Courier" , fontWeight: "bold"}}>2002</span>
                </div>
              </div>

              <div className='about__section2__card__description'>
                <p className='about__section2__card__paragraph'>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                  <br /><br />
                  Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Totam rem aperiam, eaque ipsa quae ab illo inventore veritatis et quasi architecto beatae vitae dicta sunt explicabo. Nemo enim ipsam voluptatem quia voluptas sit aspernatur aut odit aut fugit, sed quia consequuntur magni dolores eos qui ratione voluptatem sequi nesciunt. Vel illum qui dolorem eum fugiat quo voluptas nulla pariatur?
                </p>
              </div>

              <div className='about__section2__card__label__container'>
                <div className='about__section2__card__label__left'>CYBER</div>
                <div className='about__section2__card__label__right'>SECURITY</div>
              </div>

              <div className='about__section2__card__symbols'>
                <div className='about__section2__card__symbols__item'>
                  <img 
                    src={recycle_logo}
                    className="about__section2__card__symbols__recycle"
                  />
                </div>
                <div className='about__section2__card__symbols__item'>
                  <img 
                    src={weee_symbol_logo}
                    className="about__section2__card__symbols__WEEE_symbol"
                  />
                </div>
                <div className='about__section2__card__symbols__item'>
                  <img 
                    src={ce_logo}
                    className="about__section2__card__symbols__CE_logo"
                  />
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Sección 3 */}
        <section className="about__section3" ref={section3Ref}>
          <div className="about__left3">
            <h1 className="about__section3__title">Philosophy</h1>
          </div>
          <div className="about__right3">
            <p className="about__section3__description">
              I believe in writing clean, maintainable code and following best practices in software development. 
              Continuous learning and adaptation are at the core of my professional philosophy.
              <br /><br />
              When I'm not coding, you can find me exploring new technologies, contributing to open-source projects, 
              or sharing knowledge with the developer community.
            </p>
          </div>
        </section>

      </div>
    </div>
  );
};

export default About;
