import React from 'react';

// Contextos estáticos para cada sección
const sectionContexts = {
  1: require.context('../assets/imgs/home_image_section1', false, /section1_image_\d+\.jpg$/),
  2: require.context('../assets/imgs/home_image_section2', false, /section2_image_\d+\.jpg$/),
  3: require.context('../assets/imgs/home_image_section3', false, /section3_image_\d+\.jpg$/)
};

const HomeCarousel = ({ section }) => {
  const sectionNumber = section + 1;
  const images = sectionContexts[sectionNumber] 
    ? sectionContexts[sectionNumber].keys().map(key => sectionContexts[sectionNumber](key)) 
    : [];

  return (
    <div className="home__carrousel__img__rows">
      {images.map((img, index) => (
        <div
          key={index}
          className="image-part-container"
          style={{ 
            width: `${100 / images.length}%`,  // Ancho proporcional
            flexShrink: 0  // Evita compresión
          }}
        >
          <img
            src={img.default || img}
            alt={`Sección ${sectionNumber} - Parte ${index + 1}`}
            className="images-rows"
            style={{
              width: '100%',      // Ocupa el 100% del contenedor
              height: '100%',     // Altura completa
              objectFit: 'none'   // ¡Clave! Desactiva el recorte/zoom
            }}
          />
        </div>
      ))}
    </div>
  );
};

export default HomeCarousel;
