import React, { useEffect } from 'react';
import '../assets/styles/App.css';
import profile from '../assets/imgs/viper.jpg';

const Home = ({ loading, showWelcome }) => {
  useEffect(() => {
    // Desactiva el scroll al montar el componente
    document.body.style.overflow = 'hidden';

    // Restablece el scroll cuando el componente se desmonte
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, []); // El array vacío hace que esto se ejecute solo al montar y desmontar el componente

  return (
    <div className="content">
      {!loading && showWelcome && (
        <div className="welcome">
          <div className="profile">
            <img src={profile} alt="Profile" />
          </div>
          <div className='welcome-message'>
            <p><b>hey</b></p>
              <p>I'm a developer based in <b>Barcelona</b>. I create things for the apps, hacking & malware and whatever else.
                The page is still under maintenance, so feel free to look around if you want..</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
