import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Header from './components/Header';
import GrainOverlay from './components/GrainOverlay';
import Spinner from './components/Preloader';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [showWelcome, setShowWelcome] = useState(true);
  const [videoLoaded, setVideoLoaded] = useState(false);














  useEffect(() => {
    const handleContextMenu = (e) => {
      e.preventDefault();
      const rightClickMessage = document.createElement('div');
      rightClickMessage.innerText = 'Right click not possible';
      rightClickMessage.style.position = 'fixed';
      rightClickMessage.style.top = `${e.clientY}px`;
      rightClickMessage.style.left = `${e.clientX}px`;
      rightClickMessage.style.backgroundColor = 'white';
      rightClickMessage.style.padding = '5px';
      rightClickMessage.style.border = '1px solid black';
      rightClickMessage.style.zIndex = 1000;
      document.body.appendChild(rightClickMessage);

      setTimeout(() => {
        document.body.removeChild(rightClickMessage);
      }, 2000);
    };

    window.onload = function() {
      document.addEventListener("contextmenu", handleContextMenu, false);
    };

    return () => {
      document.removeEventListener("contextmenu", handleContextMenu);
    };
  }, []);
















  document.ontouchmove = function (event) {
    event.preventDefault();
  };

  useEffect(() => {
    const welcomeTimeout = setTimeout(() => {
      setShowWelcome(false);
    }, 5400);
    return () => clearTimeout(welcomeTimeout);
  }, []);

  const handleVideoLoad = () => {
    setVideoLoaded(true);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      default:
        return null;
    }
  };

  return (
    <>
      {showWelcome ? (
        <Spinner />
      ) : (
        <div>
          <GrainOverlay />
          <Header setCurrentPage={setCurrentPage} />

          <div className='home'>
            <video
              className={`videoTag ${videoLoaded ? 'visible' : 'hidden'}`}
              autoPlay
              loop
              muted
              playsInline
              onLoadedData={handleVideoLoad}
            >
              <source src={background} type="video/mp4" />
            </video>
            {renderPage()}
          </div> 
        </div>
      )}
    </>
  );
};

export default App;
