import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Testing from "./components/Testing";
import Navbar from './components/Header';
import Footer from './components/SectionFooter'; // Importa el Footer
import GrainOverlay from './components/GrainOverlay';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [showWelcome, setShowWelcome] = useState(true); // Controla si la animación de bienvenida está activa

  document.ontouchmove = function (event) {
    event.preventDefault();
  };

  useEffect(() => {
    // Ocultar la pantalla de bienvenida después de 3 segundos
    const welcomeTimeout = setTimeout(() => {
      setShowWelcome(false);
    }, 3000);

    return () => clearTimeout(welcomeTimeout); // Limpiar timeout al desmontar
  }, []);

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      case 'testing':
        return <Testing />;
      default:
        return null;
    }
  };

  return (
    <>
      {/* Pantalla de bienvenida */}
      {showWelcome ? (
        <div className="welcome-screen">
          <div className="spinner"></div>
        </div>
      ) : (
        // Contenido principal (siempre se muestra después de la bienvenida)
        <div>
          <GrainOverlay />
          <Navbar setCurrentPage={setCurrentPage} />
          <div className="home">

            {useEffect(() => {
              console.log('Welcome to my portfolio!');
            }, [])}

            <video className="videoTag" autoPlay loop muted playsInline>
              <source src={background} type="video/mp4" />
            </video>
            {renderPage()}
          </div>
        </div>
      )}
    </>
  );
};

export default App;
