import React, { createContext, useState, useEffect } from 'react';

// Creamos el contexto
export const ThemeContext = createContext();

// Proveedor del contexto
export const ThemeProvider = ({ children }) => {
  // Estado para el tema, por defecto dark
  const [theme, setTheme] = useState('dark');

  // Cambiar hoja de estilos cuando cambia el tema
  useEffect(() => {
    const existingLink = document.getElementById('theme-css');

    if (existingLink) {
      existingLink.href = theme === 'dark' 
        ? '/src/assets/styles/app-dark.css' 
        : '/src/assets/styles/app-light.css';
    } else {
      const link = document.createElement('link');
      link.id = 'theme-css';
      link.rel = 'stylesheet';
      link.href = theme === 'dark' 
        ? '/src/assets/styles/App-dark-theme.css' 
        : '/src/assets/styles/App-light-theme.css';
      document.head.appendChild(link);
    }
  }, [theme]);

  // Función para alternar el tema
  const toggleTheme = () => {
    setTheme(prev => (prev === 'dark' ? 'light' : 'dark'));
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};
