import { useEffect } from 'react';
import '../assets/styles/App.css';

const useDisableContextMenu = () => {
    useEffect(() => {
      const handleContextMenu = (e) => {
        e.preventDefault();
        document.body.style.cursor = 'not-allowed';

        const resetCursor = () => {
          document.body.style.cursor = '';
          document.removeEventListener('click', resetCursor);
          document.removeEventListener('contextmenu', resetCursor);
        };

        document.addEventListener('click', resetCursor);
        document.addEventListener('contextmenu', resetCursor);
      };

      const handleHover = (e) => {
        if (e.target.tagName === 'A' || e.target.tagName === 'BUTTON') {
          e.target.style.cursor = 'help';
        }
      };

      window.onload = function() {
        document.addEventListener("contextmenu", handleContextMenu, false);
        document.addEventListener("mouseover", handleHover, false);
      };

      return () => {
        document.removeEventListener("contextmenu", handleContextMenu);
        document.removeEventListener("mouseover", handleHover);
      };
    }, []);
  };

  export default useDisableContextMenu;
