import React, { useState, useEffect } from 'react';
import '../assets/styles/App.css';
import '../assets/styles/Nav.css';
import carrousel__img__1 from '../assets/imgs/carrousel__img__1.jpg';

const Home = () => {
  const [activeSection, setActiveSection] = useState(0);
  const [hoveredRow, setHoveredRow] = useState(null);
  const [fadeActive, setFadeActive] = useState(false);
  const totalRows = 20;

  const animationDuration = 600;
  const delayIncrement = 50;

  // Handlers de navegación
  const handlePrev = () => setActiveSection(prev => (prev - 1 + 3) % 3);
  const handleNext = () => setActiveSection(prev => (prev + 1) % 3);
  const handleDotClick = (index) => setActiveSection(index);

  // Cálculo de experiencia
  const [timeSince, setTimeSince] = useState('');
  useEffect(() => {
    const calculateTimeSince = () => {
      const specificDate = new Date('2021-10-25');
      const currentDate = new Date();
      const differenceInMs = currentDate - specificDate;

      const msInYear = 1000 * 60 * 60 * 24 * 365.25;
      const msInMonth = 1000 * 60 * 60 * 24 * 30.44;
      const msInWeek = 1000 * 60 * 60 * 24 * 7;

      const years = Math.floor(differenceInMs / msInYear);
      const remainingMsAfterYears = differenceInMs % msInYear;
      const months = Math.floor(remainingMsAfterYears / msInMonth);
      const remainingMsAfterMonths = remainingMsAfterYears % msInMonth;
      const weeks = Math.floor(remainingMsAfterMonths / msInWeek);

      setTimeSince(`T+ ${years}y : ${months}m : ${weeks}w`);
    };
    calculateTimeSince();
  }, []);

  // Contenido de las secciones
  const sections = {
    leftContent: [
      {
        banner: [
          <div key="0-1" className='banner__description__section'></div>,
          <div key="0-2" className='banner__description__section'>
            My name is David Gomez Pilipenko aka //veqqar im a software developer and cybersecurity specialist. Based in Barcelona (ES).
          </div>
        ],
        subtitle: 'Me',
        title: 'About myself',
        button: 'Visit'
      },
      {
        banner: [
          <div key="1-1" className='banner__description__section'></div>,
          <div key="1-2" className='banner__description__section'>
            Explore my technical projects and cybersecurity case studies. From full-stack applications to penetration testing reports.
          </div>
        ],
        subtitle: 'Work',
        title: 'Projects showcase',
        button: 'View'
      },
      {
        banner: [
          <div key="2-1" className='banner__description__section'></div>,
          <div key="2-2" className='banner__description__section'>
            Let's connect for opportunities in software development and cybersecurity. Available for freelance and consulting projects.
          </div>
        ],
        subtitle: 'Connect',
        title: 'Get in touch',
        button: 'Contact'
      }
    ],
    rightContent: [
      // Sección 0 - About
      <div key="0" className='home__tittle'>
        <div className='header__section'>
          hi im <span className='home__name'>veqqar</span>
        </div>

        <div className='home__carrousel' style={{ position: 'relative' }}>
          <img 
            src={carrousel__img__1} 
            alt='Carousel' 
            className='home__carrousel__img' 
          />

          <div className="home__carrousel__img__rows" onClick={() => setFadeActive(true)}>
            {Array.from({ length: totalRows }, (_, index) => {
              let blurIntensity = 0;
              let saturationIntensity = 1;

              if (hoveredRow !== null && Math.abs(index - hoveredRow) <= 3) {
                const distance = Math.abs(index - hoveredRow);
                blurIntensity = 7.5 - (distance * 1.5);
                saturationIntensity = 1 + (3 - distance) * 0.25;
              }

              return (
                <div 
                  key={index} 
                  className="home__carrousel__img__row"
                  onMouseEnter={() => setHoveredRow(index)}
                  onMouseLeave={() => setHoveredRow(null)}
                  style={{ 
                    '--blur': `${blurIntensity}px`,
                    '--saturate': saturationIntensity,
                    '--delay': `${index * delayIncrement}ms`
                  }}
                >
                  <div 
                    className="home__carrousel__img__row__inner"
                    style={fadeActive ? {
                      animation: `expandWidth ${animationDuration}ms cubic-bezier(0.4, 0, 0.2, 1) forwards`,
                      animationDelay: `${index * delayIncrement}ms`
                    } : {}}
                  />
                </div>
              );
            })}
          </div>
        </div>

        <div className="home__subtittle">
          <div className="home__subtittle_sections">
            <div className="home__subtittle_section"></div>
            <div className="home__subtittle_section">
              <div className='home__subtittle_section__tittle'>■ Experience</div>
              <div className='home__subtittle_section__content' style={{fontSize: '1rem'}}>
                {timeSince}
              </div>
            </div>
            <div className="home__subtittle_section">
              <div className='home__subtittle_section__tittle'>■ Tools</div>
              <div className='home__subtittle_section__content'>
                // C / Python / JS <br/>/ React / SQL / NoSQL <br/>/ Git / Docker / Kali <br/>/ Metasploit / Burp <br/>/ Nmap // 
              </div>
            </div>
            <div className="home__subtittle_section">
              <div className='home__subtittle_section__tittle'>■ Skills</div>
              <div className='home__subtittle_section__content'>
                // Programming <br/> / Cyber-Security <br/> / Web-Dev / UI/UX //
              </div>
            </div>
          </div>
        </div>
      </div>,

      // Sección 1 - Proyectos
      <div key="1" className='home__tittle'>
        <div className='header__section'>
          my <span className='home__name'>projects</span>
        </div>

        <div className='home__carrousel' style={{ position: 'relative' }}>
          <img 
            src={carrousel__img__1} 
            alt='Projects' 
            className='home__carrousel__img' 
          />

          <div className="project__categories">
            <div className="project__category">
              <h3>Web Development</h3>
              <p>React · Node.js · MongoDB</p>
            </div>
            <div className="project__category">
              <h3>Cybersecurity</h3>
              <p>Penetration Testing · Vulnerability Analysis</p>
            </div>
            <div className="project__category">
              <h3>Open Source</h3>
              <p>Contributions · Tools Development</p>
            </div>
          </div>
        </div>

        <div className="home__subtittle">
          <div className="home__subtittle_sections">
            <div className="home__subtittle_section"></div>
            <div className="home__subtittle_section">
              <div className='home__subtittle_section__tittle'>■ Latest</div>
              <div className='home__subtittle_section__content'>
                Security Audit Tool <br/> E-commerce Platform <br/> API Framework
              </div>
            </div>
            <div className="home__subtittle_section">
              <div className='home__subtittle_section__tittle'>■ Tech Stack</div>
              <div className='home__subtittle_section__content'>
                Python · JavaScript <br/> Docker · Kubernetes <br/> AWS · Azure
              </div>
            </div>
            <div className="home__subtittle_section">
              <div className='home__subtittle_section__tittle'>■ Status</div>
              <div className='home__subtittle_section__content'>
                Active Development <br/> Completed Projects <br/> Maintenance Phase
              </div>
            </div>
          </div>
        </div>
      </div>,

      // Sección 2 - Contacto
      <div key="2" className='home__tittle'>
        <div className='header__section'>
          contact <span className='home__name'>info</span>
        </div>

        <div className='home__carrousel' style={{ position: 'relative' }}>
          <img 
            src={carrousel__img__1} 
            alt='Contact' 
            className='home__carrousel__img' 
          />

          <div className="contact__methods">
            <div className="contact__method">
              <h3>Email</h3>
              <p>contact@veqqar.com</p>
            </div>
            <div className="contact__method">
              <h3>Social</h3>
              <p>GitHub · LinkedIn · Twitter</p>
            </div>
            <div className="contact__method">
              <h3>Location</h3>
              <p>Barcelona, Spain</p>
            </div>
          </div>
        </div>

        <div className="home__subtittle">
          <div className="home__subtittle_sections">
            <div className="home__subtittle_section"></div>
            <div className="home__subtittle_section">
              <div className='home__subtittle_section__tittle'>■ Availability</div>
              <div className='home__subtittle_section__content'>
                Freelance: Yes <br/> Consulting: Limited <br/> Collaboration: Open
              </div>
            </div>
            <div className="home__subtittle_section">
              <div className='home__subtittle_section__tittle'>■ Hours</div>
              <div className='home__subtittle_section__content'>
                Mon-Fri: 9-18 CET <br/> Emergency: 24/7 <br/> Response: 24h
              </div>
            </div>
            <div className="home__subtittle_section">
              <div className='home__subtittle_section__tittle'>■ Encryption</div>
              <div className='home__subtittle_section__content'>
                PGP Key Available <br/> Signal Protocol <br/> Secure Comms
              </div>
            </div>
          </div>
        </div>
      </div>
    ]
  };

  return (
    <div>
      <div className='left__side__content'>
        <div className='banner__description'>
          {sections.leftContent[activeSection].banner}
        </div>

        {/* Section Slider */}
        <div className='home__slider'>
          <button className='home__slidder__prev' onClick={handlePrev}>PREV</button>
          <div className='home__slidder__nav'>
            <div className='section__bar'>
              <ul className='section__links'>
                {[0, 1, 2].map(index => (
                  <li 
                    key={index}
                    className={`onClick__Section__link ${activeSection === index ? 'active' : ''}`}
                    onClick={() => handleDotClick(index)}
                  >
                    <div className='dot cursor-in-transition'></div>
                  </li>
                ))}
              </ul>
            </div>
          </div>
          <button className='home__slidder__next' onClick={handleNext}>NEXT</button>
        </div>

        <div className='home__description_subtittle'>
          {sections.leftContent[activeSection].subtitle}
        </div>
        <div className='home__description_tittle'>
          {sections.leftContent[activeSection].title}
        </div>

        <div className='description__link'>
          <button className='description__link__button'>
            {sections.leftContent[activeSection].button} <i className="fa-solid fa-square-arrow-up-right"></i>
          </button>
        </div>
      </div>

      <div className='right__side__content'>
        {sections.rightContent[activeSection]}
      </div>
    </div>
  );
};

export default Home;
