import React, { useState } from 'react';
import '../assets/styles/App.css';
import profile1 from '../assets/imgs/dot-man.gif';

const About = () => {
  const [isVisibleSection2, setIsVisibleSection2] = useState(false);
  const [isVisibleSection3, setIsVisibleSection3] = useState(false);
  const [isContentHidden, setIsContentHidden] = useState(false);

  // Función para avanzar a la segunda sección
  const handleScrollToSection2 = () => {
    const section2 = document.getElementById('target-section');
    section2.scrollIntoView({ behavior: 'smooth' });

    setTimeout(() => {
      setIsContentHidden(true); // Oculta la primera sección
      setIsVisibleSection2(true); // Muestra la segunda sección
    }, 500);
  };

  // Función para avanzar a la tercera sección
  const handleScrollToSection3 = () => {
    const section3 = document.getElementById('third-section');
    section3.scrollIntoView({ behavior: 'smooth' });

    setTimeout(() => {
      setIsVisibleSection2(false); // Oculta la segunda sección
      setIsVisibleSection3(true); // Muestra la tercera sección
    }, 500);
  };

  // Función para retroceder a la primera sección
  const handleBackToTop = () => {
    const topSection = document.getElementById('about-section');
    topSection.scrollIntoView({ behavior: 'smooth' });

    setTimeout(() => {
      setIsVisibleSection2(false); // Oculta la segunda sección
      setIsContentHidden(false); // Muestra la primera sección
    }, 500);
  };

  // Función para retroceder a la segunda sección
  const handleBackToSection2 = () => {
    const section2 = document.getElementById('target-section');
    section2.scrollIntoView({ behavior: 'smooth' });

    setTimeout(() => {
      setIsVisibleSection3(false); // Oculta la tercera sección
      setIsVisibleSection2(true); // Muestra la segunda sección
    }, 500);
  };

  return (
    <div className="content">
      {/* Primera sección (about-section) */}
      <div id="about-section" className={`hidden-content ${isContentHidden ? 'hidden' : ''}`}>
        <div className="about-1">
          <div className="about-text">
            <div className="about-bio">
              <p>I’ve studied cybersecurity and am currently diving into computer science software engineering. I’ve dabbled in a lot, like  
                pentesting, OSINT, malware dev, software dev and websites. I just kind of do it all. 
                Nothing too crazy, but there's always more to learn, though.</p>
            </div>
            <div className="about-bio-list">
              <ul>
                <li>Location: Barcelona, Spain</li>
                <li>Expertise: Software Development & Cybersecurity</li>
                <li>Hobbies: Cook</li>
                <li>Experience: <span style={{ fontFamily: 'arial', fontSize: '15px' }}>5</span>+ Years</li>
              </ul>
            </div>
          </div>

          <div className="about-bio-img">
            <img src={profile1} alt="Profile" />
          </div>
        </div>

        {/* Botón para avanzar a la segunda sección */}
        <div className="scroll-button">
          <button onClick={handleScrollToSection2}>Scroll Down to Section 2</button>
        </div>
      </div>















      {/* Segunda sección (target-section) */}
      <div id="target-section" className={`hidden-section ${isVisibleSection2 ? 'visible' : ''}`}>
        <p>This is the content of the second section!</p>

        {/* Botón para avanzar a la tercera sección */}
        <div className="scroll-button">
          <button onClick={handleScrollToSection3}>Scroll Down to Section 3</button>
        </div>

        {/* Botón para retroceder a la primera sección */}
        <div className="back-button">
          <button onClick={handleBackToTop}>Back to Top</button>
        </div>
      </div>

      {/* Tercera sección (third-section) */}
      <div id="third-section" className={`hidden-section ${isVisibleSection3 ? 'visible' : ''}`}>
        <p>This is the content of the third section!</p>

        {/* Botón para retroceder a la segunda sección */}
        <div className="back-button">
          <button onClick={handleBackToSection2}>Back to Section 2</button>
        </div>
      </div>
    </div>
  );
};

export default About;
