import React, { useEffect, useRef, useState } from 'react';
import section1Image from '../assets/imgs/carrousel_img_1.jpg';
import section2Image from '../assets/imgs/carrousel_img_2.jpg';
import section3Image from '../assets/imgs/carrousel_img_3.jpg';

const sectionImages = [section1Image, section2Image, section3Image];
const NUM_STRIPS = 20;
const ANIMATION_DURATION = 500;
const STRIP_DELAY = 30;

const HomeCarousel = ({ section }) => {
  const canvasRef = useRef(null);
  const [image, setImage] = useState(null);
  const [hoveredStrip, setHoveredStrip] = useState(null);
  const animationRef = useRef(null);
  const stripsProgress = useRef(Array(NUM_STRIPS).fill(0));
  const isAnimating = useRef(false);
  const currentStrip = useRef(null);

  // Cargar imagen actual
  useEffect(() => {
    const img = new Image();
    img.src = sectionImages[section];
    img.onload = () => {
      setImage(img);
      drawInitialState();
    };
    img.onerror = (e) => console.error('Error loading image:', e);
  }, [section]);

  const drawInitialState = () => {
    const ctx = canvasRef.current?.getContext('2d');
    if (!ctx) return;

    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    stripsProgress.current = Array(NUM_STRIPS).fill(0);
    drawImage(ctx);
  };

  // Configurar canvas y animación continua
  useEffect(() => {
    if (!image) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d', { 
      willReadFrequently: true,
      alpha: true
    });

    ctx.imageSmoothingEnabled = true;
    ctx.imageSmoothingQuality = 'high';

    const container = canvas.parentElement;

    const setCanvasSize = () => {
      canvas.width = container.clientWidth * 2;
      canvas.height = container.clientHeight * 2;
      canvas.style.width = `${container.clientWidth}px`;
      canvas.style.height = `${container.clientHeight}px`;
    };

    setCanvasSize();

    let animationFrameId;
    const animate = () => {
      // Suavizar transición del strip hover
      if (currentStrip.current !== hoveredStrip) {
        currentStrip.current = hoveredStrip !== null ? 
          currentStrip.current + (hoveredStrip - currentStrip.current) * 0.2 :
          null;
        if (currentStrip.current !== null && Math.abs(currentStrip.current - hoveredStrip) < 0.1) {
          currentStrip.current = hoveredStrip;
        }
      }

      drawImage(ctx);
      animationFrameId = requestAnimationFrame(animate);
    };

    animate();

    const resizeObserver = new ResizeObserver(setCanvasSize);
    resizeObserver.observe(container);

    return () => {
      resizeObserver.disconnect();
      cancelAnimationFrame(animationFrameId);
    };
  }, [image, hoveredStrip]);

  const getAffectedStrips = () => {
    if (currentStrip.current === null) return [];
    const center = Math.floor(currentStrip.current);
    const affectedStrips = [];

    for (let i = -3; i <= 3; i++) {
      const stripIndex = center + i;
      if (stripIndex >= 0 && stripIndex < NUM_STRIPS) {
        const distance = Math.abs(i);
        affectedStrips.push({
          index: stripIndex,
          intensity: Math.max(1 - (distance * 0.25), 0.25) // 100%, 75%, 50%, 25%
        });
      }
    }
    return affectedStrips;
  };

  const applySaturationEffect = (ctx) => {
    const affectedStrips = getAffectedStrips();
    if (affectedStrips.length === 0) return;

    const stripWidth = Math.ceil(ctx.canvas.width / NUM_STRIPS);
    const totalWidth = stripWidth * NUM_STRIPS;
    const xOffset = Math.max(0, (ctx.canvas.width - totalWidth) / 2);

    affectedStrips.forEach(({ index, intensity }) => {
      const xPos = Math.floor(xOffset + index * stripWidth);

      // Aplicar overlay de saturación
      ctx.globalCompositeOperation = 'overlay';
      ctx.fillStyle = `rgba(255, 255, 255, ${0.4 * intensity})`;
      ctx.fillRect(xPos, 0, stripWidth, ctx.canvas.height);
    });

    ctx.globalCompositeOperation = 'source-over';
  };

  const drawImage = (ctx) => {
    if (!image || !ctx) return;

    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);

    // Dibujar fondo transparente
    ctx.fillStyle = 'transparent';
    ctx.fillRect(0, 0, ctx.canvas.width, ctx.canvas.height);

    // Calcular dimensiones
    const stripWidth = Math.ceil(ctx.canvas.width / NUM_STRIPS);
    const totalWidth = stripWidth * NUM_STRIPS;
    const xOffset = Math.max(0, (ctx.canvas.width - totalWidth) / 2);

    stripsProgress.current.forEach((progress, index) => {
      ctx.save();

      const xPos = Math.floor(xOffset + index * stripWidth);
      const revealWidth = Math.ceil(stripWidth * progress);

      ctx.beginPath();
      ctx.rect(xPos, 0, revealWidth, ctx.canvas.height);
      ctx.clip();

      ctx.drawImage(
        image,
        Math.floor((index * image.width) / NUM_STRIPS),
        0,
        Math.ceil(image.width / NUM_STRIPS),
        image.height,
        xPos,
        0,
        stripWidth,
        ctx.canvas.height
      );

      ctx.restore();
    });

    applySaturationEffect(ctx);
  };

  const handleMouseMove = (e) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const stripWidth = canvas.clientWidth / NUM_STRIPS;
    const stripIndex = Math.min(Math.floor(x / stripWidth), NUM_STRIPS - 1);

    setHoveredStrip(stripIndex);
  };

  const startAnimation = () => {
    if (isAnimating.current) return;
    isAnimating.current = true;

    const animate = (startTime) => {
      const ctx = canvasRef.current?.getContext('2d');
      if (!ctx) return;

      stripsProgress.current = stripsProgress.current.map((_, index) => {
        const elapsed = performance.now() - startTime - (index * STRIP_DELAY);
        const rawProgress = Math.min(elapsed / ANIMATION_DURATION, 1);
        return 1 - Math.pow(1 - rawProgress, 3);
      });

      drawImage(ctx);

      if (performance.now() - startTime < (NUM_STRIPS * STRIP_DELAY) + ANIMATION_DURATION) {
        animationRef.current = requestAnimationFrame(() => animate(startTime));
      } else {
        isAnimating.current = false;
        stripsProgress.current = Array(NUM_STRIPS).fill(1);
        drawImage(ctx);
      }
    };

    animationRef.current = requestAnimationFrame(() => animate(performance.now()));
  };

  return (
    <div 
      className="home__carrousel" 
      onClick={startAnimation}
      onMouseMove={handleMouseMove}
      onMouseLeave={() => setHoveredStrip(null)}
      style={{ 
        position: 'relative',
        cursor: 'pointer',
        background: 'transparent'
      }}
    >
      <canvas 
        ref={canvasRef}
        style={{ 
          background: 'transparent',
          imageRendering: 'auto'
        }}
      />
    </div>
  );
};

export default HomeCarousel;
