import React, { useState } from "react";
import '../assets/styles/NavBar.css';

function Navbar({ setCurrentPage }) {
  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");
  const [animateButton, setAnimateButton] = useState(false); // State to handle the pop animation

  const navToggle = () => {
    setActive(active === "nav__menu" ? "nav__menu nav__active" : "nav__menu");
    setIcon(icon === "nav__toggler" ? "nav__toggler toggle" : "nav__toggler");
  };

  const handleNavClick = (page) => {
    setCurrentPage(page);
    setActive("nav__menu");
    setIcon("nav__toggler");
  };

  const handleHomeClick = () => {
    setAnimateButton(true); // Initiate animation
    const homeIcon = document.querySelector('.home-icon');

    // Step 1: Apply the shrink class
    homeIcon.classList.add('shrink');

    // Step 2: Wait for 300ms (duration of shrink animation)
    setTimeout(() => {
      // Step 3: Remove the shrink class and apply the expand class
      homeIcon.classList.remove('shrink');
      homeIcon.classList.add('expand');

      // Step 4: Set a timeout to remove the expand class and reset the state
      setTimeout(() => {
        homeIcon.classList.remove('expand');
        setAnimateButton(false);
        handleNavClick('home');
      }, 300); // Keep the expand effect for 300ms before navigating
    }, 300); // Wait for shrink animation to complete
  };

  return (
    <nav className="nav">
      <button
        className={`home__icon ${animateButton ? 'pop' : ''}`}
        onClick={handleHomeClick}
      >
        <span className={`home-icon`}>âœ¦</span>
      </button>
      <ul className={active}>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('about')}>
            About
          </button>
        </li>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('projects')}>
            Projects
          </button>
        </li>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('contact')}>
            Contact
          </button>
        </li>
      </ul>
      <div onClick={navToggle} className={icon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
}

export default Navbar;
