import React, { useState, useEffect, useRef } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Testing from "./components/Testing";
import Navbar from './components/Header';
import GrainOverlay from './components/GrainOverlay';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [loading, setLoading] = useState(true);

  // Ref para el video
  const videoRef = useRef(null);

  useEffect(() => {
    const videoElement = videoRef.current;

    if (videoElement) {
      // Función para manejar la carga del video
      const handleVideoLoad = () => {
        setTimeout(() => {
          setLoading(false); // Esperar 2 segundos tras la carga del video
        }, 2000);
      };

      // Escuchar el evento de carga completa
      videoElement.oncanplaythrough = handleVideoLoad;

      // Manejo de autoplay bloqueado por el navegador
      const playPromise = videoElement.play();
      if (playPromise !== undefined) {
        playPromise.catch((error) => {
          console.log('Autoplay failed, requiring user interaction:', error);
          const handleUserInteraction = () => {
            videoElement.play();
            document.removeEventListener('click', handleUserInteraction);
            document.removeEventListener('touchstart', handleUserInteraction);
          };
          document.addEventListener('click', handleUserInteraction);
          document.addEventListener('touchstart', handleUserInteraction);
        });
      }
    }

    return () => {
      // Limpieza del listener
      if (videoElement) {
        videoElement.oncanplaythrough = null;
      }
    };
  }, []);

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      case 'testing':
        return <Testing />;
      default:
        return null;
    }
  };

  return (
    <>
      {loading ? (
        // Preloader screen
        <div className="preloader">
          <div className="spinner"></div>
        </div>
      ) : (
        <>
          <GrainOverlay />
          <Navbar setCurrentPage={setCurrentPage} />
          <div className={'home'}>
            <video
              className="videoTag"
              autoPlay
              loop
              muted
              playsInline
              ref={videoRef} // Asignamos el ref al elemento video
            >
              <source src={background} type="video/mp4" />
            </video>
            {renderPage()}
          </div>
        </>
      )}
    </>
  );
};

export default App;
