import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import Navbar from './components/Header';
import GrainOverlay from './components/GrainOverlay';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [loading, setLoading] = useState(true);
  const [showWelcome, setShowWelcome] = useState(true);
  const [currentSection, setCurrentSection] = useState(1); // Sección activa de "Home"

  document.ontouchmove = function (event) {
    event.preventDefault();
  };

  useEffect(() => {
    const videoElement = document.querySelector('video');
    if (videoElement) {
      videoElement.onloadeddata = () => setLoading(false);

      const playPromise = videoElement.play();
      if (playPromise !== undefined) {
        playPromise.catch((error) => {
          console.log('Autoplay failed, requiring user interaction:', error);

          const handleUserInteraction = () => {
            videoElement.play();
            document.removeEventListener('click', handleUserInteraction);
            document.removeEventListener('touchstart', handleUserInteraction);
          };

          document.addEventListener('click', handleUserInteraction);
          document.addEventListener('touchstart', handleUserInteraction);
        });
      }
    }
  }, []);

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home loading={loading} showWelcome={showWelcome} currentSection={currentSection} />;
      default:
        return null;
    }
  };

  return (
    <>
      <GrainOverlay />
      <Navbar setCurrentPage={setCurrentPage} />
      <div className={`home ${loading ? 'loading' : 'loaded'}`}>
        <video className="videoTag" autoPlay loop muted playsInline>
          <source src={background} type="video/mp4" />
        </video>
        {renderPage()}

        {/* Footer con las secciones */}
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            color: "white",
            gap: "10rem",
            marginTop: "50px",
          }}
        >
          <ul
            style={{
              display: 'flex',
              listStyleType: 'none',
              padding: 0,
              gap: '10rem',
              fontSize: '3px'
            }}
          >
            <li onClick={() => setCurrentSection(1)}>
              <div className={currentSection === 1 ? 'dot__focused' : 'dot'}></div>
            </li>
            <li onClick={() => setCurrentSection(2)}>
              <div className={currentSection === 2 ? 'dot__focused' : 'dot'}></div>
            </li>
            <li onClick={() => setCurrentSection(3)}>
              <div className={currentSection === 3 ? 'dot__focused' : 'dot'}></div>
            </li>
          </ul>
        </div>
      </div>
    </>
  );
};

export default App;
