import React, { useState, useEffect } from 'react';
import '../assets/styles/App.css';
import profile from '../assets/imgs/viper.jpg';
import SectionFooter from './SectionFooter';

const Home = () => {
  const [currentSection, setCurrentSection] = useState(1);
  const [isAnimating, setIsAnimating] = useState(false);
  const [animationDirection, setAnimationDirection] = useState('');

  const showAlert = (origin, destination) => {
    const direction = origin < destination ? 'down' : 'up';
    alert(`Moving from section ${origin} to section ${destination} (${direction})`);
  };

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    document.body.style.touchAction = 'none';

    const handleScroll = (e) => {
      if (isAnimating) return;

      if (e.deltaY > 0 && currentSection < 3) {
        setAnimationDirection('down');
        setIsAnimating(true);
        const newSection = currentSection + 1;
        showAlert(currentSection, newSection);
        setCurrentSection(newSection);
      } else if (e.deltaY < 0 && currentSection > 1) {
        setAnimationDirection('up');
        setIsAnimating(true);
        const newSection = currentSection - 1;
        showAlert(currentSection, newSection);
        setCurrentSection(newSection);
      }
    };

    window.addEventListener('wheel', handleScroll);

    return () => {
      document.body.style.overflow = 'auto';
      document.body.style.touchAction = 'auto';
      window.removeEventListener('wheel', handleScroll);
    };
  }, [currentSection, isAnimating]);

  const handleFooterNavigation = (newSection) => {
    if (newSection !== currentSection && !isAnimating) {
      const direction = newSection > currentSection ? 'down' : 'up';
      setAnimationDirection(direction);
      setIsAnimating(true);
      showAlert(currentSection, newSection);
      setCurrentSection(newSection);
    }
  };

  const handleAnimationEnd = () => {
    setIsAnimating(false); // Desactivar la animación después de que termine
  };

  const renderSection = () => {
    const sectionClasses = `home__section${currentSection} ${animationDirection === 'down' ? 'slideOutUp' : animationDirection === 'up' ? 'slideOutDown' : ''}`;

    switch (currentSection) {
      case 1:
        return (
          <div
            className={`home__section1 ${animationDirection === 'down' ? 'slideOutUp' : animationDirection === 'up' ? 'slideInDown' : ''}`}
            onAnimationEnd={handleAnimationEnd}
          >
            <div className="section">
              <div className="profile">
                <img src={profile} alt="Profile" />
              </div>
              <div className="home__section1__message">
                <p><b>hey, im dave</b></p>
                <p>
                  i'm a developer based in <b>Barcelona</b>. I create things like&nbsp;
                  <span style={{ color: '#e96f7c' }}>sites & apps</span> and delve into&nbsp;
                  <span style={{ color: '#e96f7c' }}>hacking & malware</span> and whatever else.
                  The page is still under maintenance, so feel free to look around if you want..
                </p>
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div
            className={`home__section2 ${animationDirection === 'down' ? 'slideInUp' : animationDirection === 'up' ? 'slideOutUp' : ''}`}
            onAnimationEnd={handleAnimationEnd}
          >
            <h1 style={{ color: 'white' }}>Special Abilities</h1>
            <ul>
              <li>Desarrollo Web: Creación de sitios y aplicaciones web dinámicas y responsivas.</li>
              <li>Programación de Software: Desarrollo de aplicaciones personalizadas para diversas necesidades.</li>
              <li>Ciberseguridad: Especializado en malware pentesting y prácticas seguras de red.</li>
              <li>Redes Informáticas: Configuración y optimización de infraestructuras de red.</li>
            </ul>
            <p>
              <b>PD:</b> if you want to know more about my skills, you can contact me through my social networks.
            </p>
          </div>
        );

      case 3:
        return (
          <div
            className={`home__section3 ${animationDirection === 'down' ? 'slideInUp' : animationDirection === 'up' ? 'slideOutUp' : ''}`}
            onAnimationEnd={handleAnimationEnd}
          >
            <h1 style={{ color: 'white' }}>Some of my stuff and side projects:</h1>
            <ul>
              <li>Proyect 1: <a href="">Link</a></li>
              <li>Proyect 2: <a href="">Link</a></li>
              <li>Proyect 3: <a href="">Link</a></li>
              <li>Proyect 4: <a href="">Link</a></li>
            </ul>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="content">
      <div>
        {renderSection()}
        <SectionFooter currentSection={currentSection} setCurrentSection={handleFooterNavigation} />
      </div>
    </div>
  );
};

export default Home;
