import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Testing from "./components/Testing";
import Navbar from './components/Header';
import GrainOverlay from './components/GrainOverlay';
import Spinner from './components/Preloader'; // Importa el Spinner
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [showWelcome, setShowWelcome] = useState(true); // Inicia con la animación de bienvenida
  const [videoLoaded, setVideoLoaded] = useState(false); // Controla si el video de fondo está cargado
  const [isPreloading, setIsPreloading] = useState(true); // Controla si está en estado de precarga

  document.ontouchmove = function (event) {
    event.preventDefault();
  };

  useEffect(() => {
    const welcomeTimeout = setTimeout(() => {
      setShowWelcome(false); // Termina la animación de bienvenida después de 5 segundos
    }, 5000);

    // Esperar a que termine la animación de preloader y luego cargar la página
    const preloaderTimeout = setTimeout(() => {
      setIsPreloading(false); // Finaliza el preloader después de 4 segundos
    }, 4000);

    return () => {
      clearTimeout(welcomeTimeout);
      clearTimeout(preloaderTimeout);
    }; // Limpiar timeouts al desmontar
  }, []);

  const handleVideoLoad = () => {
    setVideoLoaded(true);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      case 'testing':
        return <Testing />;
      default:
        return null;
    }
  };

  return (
    <>
      {showWelcome ? (
        <Spinner />
      ) : (
        <div>
          {/* Renderiza el preloader o la página según el estado de precarga */}
          {isPreloading ? (
            <Spinner />
          ) : (
            <div>
              <GrainOverlay isVisible={!isPreloading} /> {/* Solo muestra el GrainOverlay cuando no esté en precarga */}
              <Navbar setCurrentPage={setCurrentPage} />

              <div className='home'>
                <video
                  className={`videoTag ${videoLoaded ? 'visible' : 'hidden'}`}
                  autoPlay
                  loop
                  muted
                  playsInline
                  onLoadedData={handleVideoLoad}
                >
                  <source src={background} type="video/mp4" />
                </video>
                {renderPage()}
              </div>
            </div>
          )}
        </div>
      )}
    </>
  );
};

export default App;
