import React, { useEffect, useState } from 'react';
import '../assets/styles/App.css';

const MouseComponent = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [trailingPosition, setTrailingPosition] = useState({ x: 0, y: 0 });
  const [isHoveringLink, setIsHoveringLink] = useState(false);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setPosition({ x: e.clientX, y: e.clientY });
    };

    const handleLinkHover = () => {
      setIsHoveringLink(true);
    };

    const handleLinkLeave = () => {
      setIsHoveringLink(false);
    };

    document.addEventListener('mousemove', handleMouseMove);

    const links = document.querySelectorAll('a');
    links.forEach(link => {
      link.addEventListener('mouseenter', handleLinkHover);
      link.addEventListener('mouseleave', handleLinkLeave);
    });

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      links.forEach(link => {
        link.removeEventListener('mouseenter', handleLinkHover);
        link.removeEventListener('mouseleave', handleLinkLeave);
      });
    };
  }, []);

  useEffect(() => {
    const moveTrailingCursor = () => {
      const dx = position.x - trailingPosition.x;
      const dy = position.y - trailingPosition.y;
      setTrailingPosition({
        x: trailingPosition.x + dx * 0.5, // Adjust the 0.1 for more/less lag
        y: trailingPosition.y + dy * 0.5,
      });
    };

    const animationFrame = requestAnimationFrame(moveTrailingCursor);
    return () => cancelAnimationFrame(animationFrame);
  }, [position, trailingPosition]);

  return (
    <div
      className="custom-cursor"
      style={{
        left: `${trailingPosition.x}px`,
        top: `${trailingPosition.y}px`,
      }}
    >
      <i className={isHoveringLink ? 'fa-solid fa-circle-right' : 'fa-solid fa-circle'}></i>
    </div>
  );
};

export default MouseComponent;
