import React, { useState, useEffect } from 'react';
import GrainOverlay from './components/GrainOverlay';
import Spinner from './components/Preloader';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [showWelcome, setShowWelcome] = useState(true);
  const [videoLoaded, setVideoLoaded] = useState(false);
  const [currentSection, setCurrentSection] = useState(1);
  const [menuActive, setMenuActive] = useState(false);

  useEffect(() => {
    const welcomeTimeout = setTimeout(() => setShowWelcome(false), 5400);
    return () => clearTimeout(welcomeTimeout);
  }, []);

  const renderSection = () => {
    const sections = [
      <div key="1" className="section">
        <h1>Welcome to Core Experience</h1>
        <p>Essential digital foundation with minimal distractions.</p>
      </div>,
      <div key="2" className="section">
        <h1>Features & Capabilities</h1>
        <p>Discover the core functionalities that power this experience.</p>
      </div>,
      <div key="3" className="section">
        <h1>Connect & Explore</h1>
        <p>Ready to dive deeper? Let's interact.</p>
      </div>
    ];

    return sections[currentSection - 1];
  };

  return (
    <>
      {showWelcome ? (
        <Spinner />
      ) : (
        <div className="app-container">
          <GrainOverlay />

          {/* Simplified Navbar */}
          <nav className="nav">
            <button className="home__icon">
              <span className="home-icon">âœ¦</span>
            </button>
            <div 
              className={`nav__toggler ${menuActive ? 'active' : ''}`} 
              onClick={() => setMenuActive(!menuActive)}
            >
              <div className="line"></div>
              <div className="line"></div>
              <div className="line"></div>
            </div>
          </nav>

          <video
            className={`video-background ${videoLoaded ? 'visible' : ''}`}
            autoPlay loop muted playsInline
            onLoadedData={() => setVideoLoaded(true)}
          >
            <source src={background} type="video/mp4" />
          </video>

          <div className="content">
            {renderSection()}
            <SectionSlider 
              currentSection={currentSection}
              setCurrentSection={setCurrentSection}
            />
          </div>
        </div>
      )}
    </>
  );
};

const SectionSlider = ({ currentSection, setCurrentSection }) => (
  <div className="section-slider">
    {[1, 2, 3].map((num) => (
      <button
        key={num}
        className={`slider-dot ${currentSection === num ? 'active' : ''}`}
        onClick={() => setCurrentSection(num)}
      />
    ))}
  </div>
);

export default App;
