import React, { useEffect } from 'react';
import SphereAnimation from './extra_functions/SphereAnimation';

const Contact = () => {

  useEffect(() => {
    const bg = document.querySelector('.background-video');
    const grain = document.querySelector('.grain-overlay');
    const appEl = document.querySelector('.app') || document.documentElement;

    const prevBgDisplay = bg ? bg.style.display : null;
    const prevAppBg = appEl ? appEl.style.background : null;
    const prevGrainDisplay = grain ? grain.style.display : null;
    const prevGrainOpacity = grain ? grain.style.opacity : null;

    if (bg) {
      bg.classList.add('fade-out-for-contact');

      const hideTimeout = setTimeout(() => {
        bg.style.display = 'none';
      }, 420);

      if (grain) {
        grain.style.display = 'block';
        grain.style.opacity = '0.2';
      }

      if (appEl) {
        appEl.style.background = '#000';
      }

      return () => {
        clearTimeout(hideTimeout);

        if (bg) {
          bg.style.display = prevBgDisplay || 'block';
          bg.offsetHeight;
          bg.classList.remove('fade-out-for-contact');
        }

        if (grain) {
          grain.style.display = prevGrainDisplay || '';
          grain.style.opacity = prevGrainOpacity || '';
        }

        if (appEl) {
          appEl.style.background = prevAppBg || '';
        }
      };
    } else {
      if (grain) {
        grain.style.display = 'block';
        grain.style.opacity = '0.2';
      }
      if (appEl) {
        appEl.style.background = '#000';
      }
      return () => {
        if (grain) {
          grain.style.display = prevGrainDisplay || '';
          grain.style.opacity = prevGrainOpacity || '';
        }
        if (appEl) {
          appEl.style.background = prevAppBg || '';
        }
      };
    }
  }, []);

  return (
    <div className="contact-container">
      <div className="canvas-wrapper">
        <div className="canvas-container">
          <SphereAnimation />
        </div>
      </div>

      <div className="content-wrapper">
        <div className="contact-content about-card-like">
          <h1 className="contact-title about__section4__header__title">/ &gt; contact</h1>
          <div className="contact-body">
            <p className="contact-description">
              ¿Interesado en colaborar o simplemente decir hola? — envíame un mensaje.
              Respondo rápido y con gusto. Puedes encontrar mis enlaces en la sección "About".
            </p>

            <div className="contact-details">
              <div className="contact-item">
                <span className="contact-item-title">Email</span><br />
                <span className="contact-item-value">tu.email@ejemplo.com</span>
              </div>

              <div className="contact-item">
                <span className="contact-item-title">LinkedIn</span><br />
                <a className="contact-item-value" href="https://www.linkedin.com/in/david-gomp/" target="_blank" rel="noreferrer">linkedin.com/in/david-gomp</a>
              </div>

              <div className="contact-item">
                <span className="contact-item-title">GitHub</span><br />
                <a className="contact-item-value" href="https://github.com/veqqar" target="_blank" rel="noreferrer">github.com/veqqar</a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
