import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Navbar from './components/Header';
import GrainOverlay from './components/GrainOverlay';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [loading, setLoading] = useState(true);
  const [showWelcome, setShowWelcome] = useState(true);

  useEffect(() => {
    const videoElement = document.querySelector('video');

    const playVideo = () => {
      if (videoElement && videoElement.paused) {
        videoElement.muted = true; // Ensure the video is muted
        videoElement.play().catch((error) => {
          console.error('Autoplay failed, requiring user interaction:', error);
        });
      }
    };

    if (videoElement) {
      videoElement.onloadeddata = () => {
        setLoading(false);
        setShowWelcome(true);
        playVideo(); // Try to play the video after loading
      };
    }

    // For iOS: Add event listener for user interaction fallback
    document.addEventListener('touchstart', playVideo);

    return () => {
      document.removeEventListener('touchstart', playVideo);
    };
  }, []);

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home loading={loading} showWelcome={showWelcome} />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      default:
        return null;
    }
  };

  return (
    <>
      <GrainOverlay />
      <Navbar setCurrentPage={setCurrentPage} />
      <div className={`home ${loading ? 'loading' : 'loaded'}`}>
        <video className="videoTag" autoPlay loop muted playsInline>
          <source src={background} type="video/mp4" />
        </video>
        {renderPage()}
      </div>
    </>
  );
};

export default App;
