import React, { useState } from "react";
import '../assets/styles/NavBar.css';

function Navbar() {
  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");
  const [delayedActive, setDelayedActive] = useState("nav__menu");
  const navToggle = () => {
    if (active === "nav__menu") {
      // Set the icon and active state immediately
      setIcon("nav__toggler toggle");

      // Introduce a 1-second delay for activating the nav menu
      setTimeout(() => {
        setActive("nav__menu nav__active");
        setDelayedActive("nav__menu nav__active"); // This controls the actual rendering delay
      }, 1000); // 1000ms = 1 second
    } else {
      setIcon("nav__toggler");
      setActive("nav__menu");
      setDelayedActive("nav__menu"); // Reset immediately when closing
    }
  };
  return (

    <nav className="nav">
      <a href='/' className="home__icon">âœ¦</a>
      <ul className={active}>
        <li className="nav__item">
          <a href="/about" className="nav__link">
            About
          </a>
        </li>
        <li className="nav__item">
          <a href="/projects" className="nav__link">
            Projects
          </a>
        </li>
        <li className="nav__item">
          <a href="/contact" className="nav__link">
            Contact
          </a>
        </li>
      </ul>
      <div onClick={navToggle} className={icon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
}

export default Navbar;
