import React, { useState } from 'react';
import '../assets/styles/App.css';
import SectionFooter from './SectionFooter';
// import profile1 from '../assets/imgs/dot-man.gif';
import profile1 from '../assets/imgs/max-headroom.gif';
import '@fortawesome/fontawesome-free/css/all.min.css';
<script src="https://kit.fontawesome.com/b5c57f8a6b.js" crossorigin="anonymous"></script>




const About = () => {
  const [currentSection, setCurrentSection] = useState(1);

  const renderSection = () => {
    switch (currentSection) {
      case 1:
        return (
          <div className="about-1">
            <div className="about-text">
              <div className="about-profile">
                <div className="about-profile-img">
                  <img src={profile1} alt="Profile" />
                </div>
                <div className="about-profile-list">
                  <ul>
                    <li><b><span style={{ color: '#e96f7c' }}>Location:</span></b> Barcelona, Spain</li>
                    <li><b><span style={{ color: '#e96f7c' }}>Expertise:</span></b> Software Dev & Cybersec</li>
                    <li><b><span style={{ color: '#e96f7c' }}>Hobbies:</span></b> Cook</li>
                    <li><b><span style={{ color: '#e96f7c' }}>Experience:</span></b> <span style={{ fontFamily: 'arial', fontSize: '15px' }}>5</span>+ Years</li>
                  </ul>
                </div>
              </div>
              <div className="about-bio">
                <p>I’ve studied cybersecurity and am currently diving into computer science software engineering. I’ve dabbled in a lot, like  
                  pentesting, OSINT, malware dev, software dev and websites. I just kind of do it all. 
                  Nothing too crazy, but there's always more to learn, though.</p>
              </div>









            </div>
          </div>
        );







      case 2:
        return <div className="section">
          <h1 style={{color:'white'}}>Section II Content</h1>
        </div>;


      case 3:
        return <div className="section">
          <h1 style={{color:'white'}}>Section III Content</h1>
        </div>;


      default:
        return null;
    }
  };

  return (
    <div className="content">

      {renderSection()}

      <SectionFooter currentSection={currentSection} setCurrentSection={setCurrentSection} />


    </div>
  );
};

export default About;
