import React, { useState, useEffect, useRef, useCallback } from 'react';
import '../assets/styles/Nav.css';
import '../assets/styles/App.css';

const Projects = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const listRef = useRef(null);
  const itemRefs = useRef([]);
  const isAutoScrollingRef = useRef(false);
  const rafRef = useRef(null);
  const scrollEndTimeoutRef = useRef(null);
  const latestClosestRef = useRef(0);

  const projects = [
    { title: "NexusFlow", infoFormatted: <>June <span className="project__info-numbers">23</span> // Design UI & UX</> },
    { title: "QuantumCore", infoFormatted: <>April <span className="project__info-numbers">22</span> // Software Dev</> },
    { title: "ShadowNet", infoFormatted: <>March <span className="project__info-numbers">24</span> // Malware Analysis</> },
    { title: "VulnScan Pro", infoFormatted: <>November <span className="project__info-numbers">19</span> // Pentesting</> },
    { title: "CroCoToolz", infoFormatted: <>September <span className="project__info-numbers">25</span> // CyberSec Tools Dev</> },
    { title: "MobiSecure", infoFormatted: <>January <span className="project__info-numbers">23</span> // Mobile App Development</> },
    { title: "CloudShield", infoFormatted: <>July <span className="project__info-numbers">22</span> // Cloud Infrastructure</> },
    { title: "DataCrypt", infoFormatted: <>August <span className="project__info-numbers">23</span> // Encryption Systems</> },
    { title: "NetWatch", infoFormatted: <>December <span className="project__info-numbers">24</span> // Network Security</> },
    { title: "SecureComm", infoFormatted: <>May <span className="project__info-numbers">23</span> // Secure Communications</> }
  ];

  const centerItem = useCallback((index) => {
    const item = itemRefs.current[index];
    const list = listRef.current;
    if (!item || !list) return;

    // evitar reentradas
    isAutoScrollingRef.current = true;

    item.scrollIntoView({ behavior: 'smooth', block: 'center', inline: 'nearest' });

    setActiveIndex(index);

    window.setTimeout(() => {
      isAutoScrollingRef.current = false;
    }, 500);
  }, []);

  useEffect(() => {
    const t = setTimeout(() => centerItem(0), 300);
    return () => clearTimeout(t);
  }, [centerItem]);

  const computeClosestIndex = useCallback(() => {
    const list = listRef.current;
    if (!list || itemRefs.current.length === 0) return 0;

    const center = list.scrollTop + list.clientHeight / 2;
    let closestIndex = 0;
    let minDist = Infinity;

    itemRefs.current.forEach((item, index) => {
      if (!item) return;
      const itemCenter = item.offsetTop + item.offsetHeight / 2;
      const dist = Math.abs(itemCenter - center);
      if (dist < minDist) {
        minDist = dist;
        closestIndex = index;
      }
    });

    return closestIndex;
  }, []);

  const handleScroll = useCallback(() => {
    if (isAutoScrollingRef.current) return;

    if (rafRef.current) cancelAnimationFrame(rafRef.current);

    rafRef.current = requestAnimationFrame(() => {
      const closest = computeClosestIndex();
      latestClosestRef.current = closest;

      setActiveIndex(prev => (prev === closest ? prev : closest));
    });

    if (scrollEndTimeoutRef.current) clearTimeout(scrollEndTimeoutRef.current);
    scrollEndTimeoutRef.current = setTimeout(() => {
      const toCenter = latestClosestRef.current;
      if (!isAutoScrollingRef.current) {
        isAutoScrollingRef.current = true;
        centerItem(toCenter);
        setTimeout(() => { isAutoScrollingRef.current = false; }, 600);
      }
    }, 120);
  }, [centerItem, computeClosestIndex]);

  useEffect(() => {
    const list = listRef.current;
    if (!list) return;
    list.addEventListener('scroll', handleScroll, { passive: true });
    return () => {
      list.removeEventListener('scroll', handleScroll);
      if (rafRef.current) cancelAnimationFrame(rafRef.current);
      if (scrollEndTimeoutRef.current) clearTimeout(scrollEndTimeoutRef.current);
    };
  }, [handleScroll]);

  const handleItemClick = (index) => {
    centerItem(index);
  };

  return (
    <div className="projects__container">
      <div className='projects__list' ref={listRef}>
        {projects.map((project, index) => (
          <div
            key={index}
            className={`project__item ${activeIndex === index ? 'active' : ''}`}
            ref={el => itemRefs.current[index] = el}
            onClick={() => handleItemClick(index)}
          >
            <div className='project__title'>
              <a 
                // set the href link as a variable
                href={`https://${project.title}`}
                target="_blank" 
                rel="noopener noreferrer"
                style={{ color: 'inherit', textDecoration: 'none' }}
              >
                {project.title}
              </a>

            </div>
            <div className='project__info'>{project.infoFormatted}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Projects;
