import React, { useState } from 'react';
import SectionSlider from './SectionSlider';
import '../assets/styles/App.css';

const Home = () => {
  const [currentSection, setCurrentSection] = useState(1);

  const sections = [
    {
      content: <>
        <h1>Hey, I'm Dave</h1>
        <p>Developer based in Barcelona creating digital experiences</p>
      </>
    },
    {
      content: <>
        <h1>Skills & Expertise</h1>
        <p>Full-stack development, cybersecurity, and system architecture</p>
      </>
    },
    {
      content: <>
        <h1>Projects & Work</h1>
        <p>Selected case studies and personal experiments</p>
      </>
    }
  ];

  return (
    <div className="content">
      <div className={`section section-${currentSection}`}>
        {sections[currentSection - 1].content}
      </div>
      <SectionSlider currentSection={currentSection} setCurrentSection={setCurrentSection} />
    </div>
  );
};

export default Home;
