import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Testing from "./components/Testing";
import Navbar from './components/Header';
import Footer from './components/SectionFooter'; // Importa el Footer
import GrainOverlay from './components/GrainOverlay';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [loading, setLoading] = useState(true); // Controla si el video está cargando
  const [showWelcome, setShowWelcome] = useState(true); // Controla si la animación de bienvenida está activa

  document.ontouchmove = function (event) {
    event.preventDefault();
  };

  useEffect(() => {
    const videoElement = document.querySelector('video');

    if (videoElement) {
      videoElement.onloadeddata = () => {
        setLoading(false); // Cambia a "loaded" cuando el video esté listo
      };

      const playPromise = videoElement.play();
      if (playPromise !== undefined) {
        playPromise.catch((error) => {
          console.log('Autoplay failed, requiring user interaction:', error);

          // Fallback de interacción del usuario
          const handleUserInteraction = () => {
            videoElement.play();
            document.removeEventListener('click', handleUserInteraction);
            document.removeEventListener('touchstart', handleUserInteraction);
          };

          document.addEventListener('click', handleUserInteraction);
          document.addEventListener('touchstart', handleUserInteraction);
        });
      }
    }

    // Ocultar pantalla de bienvenida después de 3 segundos
    const welcomeTimeout = setTimeout(() => {
      setShowWelcome(false);
    }, 3000);

    return () => clearTimeout(welcomeTimeout); // Limpiar timeout al desmontar
  }, []);

  // Combinar estados para mostrar el contenido principal solo cuando ambos estados estén listos
  const isContentReady = !loading && !showWelcome;

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home loading={loading} showWelcome={showWelcome} />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      case 'testing':
        return <Testing />;
      default:
        return null;
    }
  };

  return (
    <>
      {/* Pantalla de bienvenida */}
      {showWelcome ? (
        <div className="welcome-screen">
          <div className="spinner"></div>
        </div>
      ) : (
        // Contenido principal (sólo se renderiza cuando el video esté cargado)
        isContentReady && (
          <>
            <GrainOverlay />
            <Navbar setCurrentPage={setCurrentPage} />
            <div className={`home ${loading ? 'loading' : 'loaded'}`}>
              <video className="videoTag" autoPlay loop muted playsInline>
                <source src={background} type="video/mp4" />
              </video>
              {renderPage()}
            </div>
          </>
        )
      )}
    </>
  );
};

export default App;
