import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Header from './components/Header';
import GrainOverlay from './components/GrainOverlay';
import Spinner from './components/Preloader';
import useDisableContextMenu from './components/AdditionalStuff';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [showWelcome, setShowWelcome] = useState(true);
  const [videoLoaded, setVideoLoaded] = useState(false);

  // useDisableContextMenu();

  document.ontouchmove = function (event) {
    event.preventDefault();
  };

  useEffect(() => {
    const welcomeTimeout = setTimeout(() => {
      setShowWelcome(false);
    }, 5400);
    return () => clearTimeout(welcomeTimeout);
  }, []);

  const handleVideoLoad = () => {
    setVideoLoaded(true);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      default:
        return null;
    }
  };

  return (
    <>
      {showWelcome ? (
        <Spinner />
      ) : (
        <div>
          <GrainOverlay />
          <Header setCurrentPage={setCurrentPage} />

          <div className='home'>
            <video
              className={`videoTag ${videoLoaded ? 'visible' : 'hidden'} ${currentPage === 'contact' ? 'dimmed' : ''}`}
              autoPlay
              loop
              muted
              playsInline
              onLoadedData={handleVideoLoad}
            >
              <source src={background} type="video/mp4" />
            </video>
            {renderPage()}
          </div> 
        </div>
      )}
    </>
  );
};

export default App;
