import React, { useState, useEffect, useRef } from 'react';

const DecryptedText = ({
  text,
  speed = 50,
  maxIterations = 10,
  characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz!@#$%^&*()_+",
  className = "",
  parentClassName = "",
  encryptedClassName = "",
  animateOn = "hover",
  revealDirection = "left"
}) => {
  const [displayText, setDisplayText] = useState(text);
  const [isDecrypting, setIsDecrypting] = useState(false);
  const [isEncrypted, setIsEncrypted] = useState(true);
  const [hasAnimated, setHasAnimated] = useState(false);
  const elementRef = useRef(null);

  const cryptoChars = characters;

  const decrypt = () => {
    if (isDecrypting || !isEncrypted) return;

    setIsDecrypting(true);
    let iteration = 0;

    const interval = setInterval(() => {
      setDisplayText(prev => {
        let result = "";

        for (let i = 0; i < text.length; i++) {
          if (i < iteration) {
            result += text[i];
          } else {
            const randomIndex = Math.floor(Math.random() * cryptoChars.length);
            result += cryptoChars[randomIndex];
          }
        }

        return result;
      });

      iteration += 1 / maxIterations;

      if (iteration >= text.length) {
        clearInterval(interval);
        setIsDecrypting(false);
        setIsEncrypted(false);
      }
    }, speed);
  };

  const handleHover = () => {
    if (animateOn === 'hover' && isEncrypted && !isDecrypting) {
      decrypt();
    }
  };

  useEffect(() => {
    if (animateOn === 'view' && !hasAnimated) {
      const observer = new IntersectionObserver(
        ([entry]) => {
          if (entry.isIntersecting) {
            setHasAnimated(true);
            decrypt();
          }
        },
        { threshold: 0.5 }
      );

      if (elementRef.current) {
        observer.observe(elementRef.current);
      }

      return () => {
        if (elementRef.current) {
          observer.unobserve(elementRef.current);
        }
      };
    }
  }, [animateOn, hasAnimated]);

  return (
    <span
      ref={elementRef}
      className={`${parentClassName} ${isEncrypted ? encryptedClassName : className}`}
      onMouseEnter={handleHover}
      style={{ cursor: isEncrypted ? 'pointer' : 'default' }}
    >
      {displayText}
    </span>
  );
};

export default DecryptedText;
