import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import GrainOverlay from './components/GrainOverlay';
import Spinner from './components/Preloader';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [showWelcome, setShowWelcome] = useState(true);
  const [videoLoaded, setVideoLoaded] = useState(false);

  useEffect(() => {
    const welcomeTimeout = setTimeout(() => {
      setShowWelcome(false);
    }, 5400);
    return () => clearTimeout(welcomeTimeout);
  }, []);

  const handleVideoLoad = () => setVideoLoaded(true);

  return (
    <>
      {showWelcome ? (
        <Spinner />
      ) : (
        <div>
          <GrainOverlay />
          <Navbar />
          <div className='home'>
            <video
              className={`videoTag ${videoLoaded ? 'visible' : 'hidden'}`}
              autoPlay loop muted playsInline
              onLoadedData={handleVideoLoad}
            >
              <source src={background} type="video/mp4" />
            </video>
            <Home />
          </div> 
        </div>
      )}
    </>
  );
};

const Navbar = () => {
  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");

  const navToggle = () => {
    setActive(active === "nav__menu" ? "nav__menu nav__active" : "nav__menu");
    setIcon(icon === "nav__toggler" ? "nav__toggler toggle" : "nav__toggler");
  };

  return (
    <nav className="nav">
      <button className="home__icon">
        <span className="home-icon">âœ¦</span>
      </button>
      <div onClick={navToggle} className={icon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
};

export default App;
