import React, { useRef, useEffect } from "react";
import * as THREE from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";

export default function CircleXZAnimation() {
  const mountRef = useRef(null);
  const sphereGroupRef = useRef(null);
  const controlsRef = useRef(null);

  useEffect(() => {
    const mount = mountRef.current;
    if (!mount) return;

    const scene = new THREE.Scene();
    scene.background = null;

    const width = mount.clientWidth;
    const height = mount.clientHeight;

    const camera = new THREE.PerspectiveCamera(55, width / height, 0.1, 1000);
    camera.position.set(0, 140, 260);
    camera.lookAt(0, 50, 0);

    const renderer = new THREE.WebGLRenderer({ 
      antialias: true,
      alpha: true
    });
    renderer.setSize(width, height);
    renderer.setPixelRatio(window.devicePixelRatio || 1);
    renderer.setClearColor(0x000000, 0);
    mount.appendChild(renderer.domElement);

    // Luces
    const amb = new THREE.AmbientLight(0xffffff, 0.55);
    scene.add(amb);
    const dir = new THREE.DirectionalLight(0xffffff, 0.4);
    dir.position.set(50, 120, 80);
    scene.add(dir);

    // Parámetros de la esfera
    const R = 50;
    const Y_MIN = 0;
    const Y_MAX = 100;
    const segments = 256;
    const NUM_CIRCLES = 20;

    const circles = [];

    // Material único para todas las circunferencias
    const circleMaterial = new THREE.LineBasicMaterial({
      color: 0x7be495,
      transparent: true,
      opacity: 0.6
    });

    // Crear un grupo para contener todos los círculos
    const sphereGroup = new THREE.Group();
    scene.add(sphereGroup);
    sphereGroupRef.current = sphereGroup;

    function createCircle() {
      const positions = new Float32Array((segments + 1) * 3);
      const geometry = new THREE.BufferGeometry();
      geometry.setAttribute("position", new THREE.BufferAttribute(positions, 3));
      geometry.setDrawRange(0, segments + 1);

      const line = new THREE.Line(geometry, circleMaterial);
      sphereGroup.add(line);

      return { line, geometry, positions };
    }

    function updateCircleGeometry(circle, radius) {
      const positions = circle.positions;
      for (let i = 0; i <= segments; i++) {
        const t = (i / segments) * Math.PI * 2;
        const x = radius * Math.cos(t);
        const z = radius * Math.sin(t);
        const idx = i * 3;
        positions[idx] = x;
        positions[idx + 1] = 0;
        positions[idx + 2] = z;
      }
      circle.geometry.attributes.position.needsUpdate = true;
    }

    function sphereRadiusAtY(yPos) {
      const val = R * R - (yPos - R) * (yPos - R);
      return val > 0 ? Math.sqrt(val) : 0;
    }

    // Crear círculos con offsets iniciales
    const range = Y_MAX - Y_MIN;
    for (let i = 0; i < NUM_CIRCLES; i++) {
      const c = createCircle();
      c.initialOffset = (i / NUM_CIRCLES) * range;
      circles.push(c);
    }

    // CONTROLES - Ahora controlan el grupo de la esfera en lugar de la cámara
    const controls = new OrbitControls(camera, renderer.domElement);
    controlsRef.current = controls;

    // Desactivar todos los controles de la cámara
    controls.enablePan = false;
    controls.enableZoom = false;
    controls.enableRotate = false;

    // Configurar controles para la esfera
    controls.target = sphereGroup.position; // El objetivo es la esfera
    controls.autoRotate = false;
    controls.update();

    // Variables para rotación manual de la esfera
    let isMouseDown = false;
    let previousMousePosition = { x: 0, y: 0 };

    // Manejar eventos de mouse para rotar la esfera
    function onMouseDown(event) {
      isMouseDown = true;
      previousMousePosition = {
        x: event.clientX,
        y: event.clientY
      };
    }

    function onMouseMove(event) {
      if (!isMouseDown) return;

      const deltaX = event.clientX - previousMousePosition.x;
      const deltaY = event.clientY - previousMousePosition.y;

      // Rotar la esfera en base al movimiento del mouse
      sphereGroup.rotation.y += deltaX * 0.01; // Rotación horizontal
      sphereGroup.rotation.x += deltaY * 0.01; // Rotación vertical

      previousMousePosition = {
        x: event.clientX,
        y: event.clientY
      };
    }

    function onMouseUp() {
      isMouseDown = false;
    }

    // Añadir event listeners para rotación manual
    renderer.domElement.addEventListener('mousedown', onMouseDown);
    renderer.domElement.addEventListener('mousemove', onMouseMove);
    renderer.domElement.addEventListener('mouseup', onMouseUp);
    renderer.domElement.addEventListener('mouseleave', onMouseUp);

    // Añadir controles de teclado para más control
    function onKeyDown(event) {
      switch(event.key) {
        case 'ArrowLeft':
          sphereGroup.rotation.y += 0.1;
          break;
        case 'ArrowRight':
          sphereGroup.rotation.y -= 0.1;
          break;
        case 'ArrowUp':
          sphereGroup.rotation.x += 0.1;
          break;
        case 'ArrowDown':
          sphereGroup.rotation.x -= 0.1;
          break;
        case 'r': // Reset rotation
          sphereGroup.rotation.set(0, 0, 0);
          break;
      }
    }

    window.addEventListener('keydown', onKeyDown);

    // Animación de los círculos
    const startTime = performance.now();
    const speed = 6;

    let rafId = null;

    function animate() {
      const now = performance.now();
      const elapsed = (now - startTime) / 1000;

      for (let i = 0; i < NUM_CIRCLES; i++) {
        const c = circles[i];
        const posInRange = (c.initialOffset + elapsed * speed) % range;
        const yPos = Y_MIN + posInRange;

        const r = sphereRadiusAtY(yPos);
        updateCircleGeometry(c, r);
        c.line.position.y = yPos;
      }

      renderer.render(scene, camera);
      rafId = requestAnimationFrame(animate);
    }

    rafId = requestAnimationFrame(animate);

    function handleResize() {
      const w = mount.clientWidth;
      const h = mount.clientHeight;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    }

    window.addEventListener("resize", handleResize);

    // Limpieza
    return () => {
      window.removeEventListener("resize", handleResize);
      window.removeEventListener('keydown', onKeyDown);

      if (rafId) cancelAnimationFrame(rafId);

      // Remover event listeners
      renderer.domElement.removeEventListener('mousedown', onMouseDown);
      renderer.domElement.removeEventListener('mousemove', onMouseMove);
      renderer.domElement.removeEventListener('mouseup', onMouseUp);
      renderer.domElement.removeEventListener('mouseleave', onMouseUp);

      // Limpiar recursos
      circles.forEach((c) => {
        c.geometry.dispose();
      });

      circleMaterial.dispose();
      if (controlsRef.current) controlsRef.current.dispose();
      renderer.dispose();

      if (renderer.domElement && mount.contains(renderer.domElement)) {
        mount.removeChild(renderer.domElement);
      }
    };
  }, []);

  return (
    <div ref={mountRef} className="canvas-container"></div>
  );
}
