import React from 'react';
import '../assets/styles/App.css';
import profile from '../assets/imgs/viper.jpg';

const About = () => {
  return (
    <div className="content">
      <div className="about-text">
        <div className="about-bio">
        <p>
          I'm a passionate developer based in <b>Barcelona, Spain</b>, with a
          love for building digital experiences. My journey started with simple websites, and now I focus on more complex projects. I enjoy solving problems, learning new technologies, and creating innovative solutions for the web. In my spare time, I dive into cyber security, hacking, and malware analysis.
        </p>
        </div>
        <div className="about-details">
        <ul>
            <li><b>Location:</b> Barcelona, Spain</li>
            <li><b>Experience:</b> 5+ years in web development</li>
            <li><b>Specialties:</b> Full-stack development, cyber security</li>
            <li><b>Interests:</b> Hacking, AI, blockchain technology</li>
          </ul>
        </div>
      </div>
      {/* <div className="about-image">
        <img src={profile} alt="Profile" />
      </div> */}
    </div>
  );
};

export default About;
