import React, { useEffect, useRef, useState } from 'react';
import section1Image from '../assets/imgs/carrousel_img_1.jpg';
import section2Image from '../assets/imgs/carrousel_img_2.jpg';
import section3Image from '../assets/imgs/carrousel_img_3.jpg';

const sectionImages = [section1Image, section2Image, section3Image];
const NUM_STRIPS = 20;
const ANIMATION_DURATION = 800;
const STRIP_DELAY = 40;

const HomeCarousel = ({ section }) => {
  const canvasRef = useRef(null);
  const [image, setImage] = useState(null);
  const animationRef = useRef(null);
  const stripsProgress = useRef(Array(NUM_STRIPS).fill(1));
  const isAnimating = useRef(false);

  // Easing function para efecto de desaceleración
  const easeOutCubic = (t) => {
    return 1 - Math.pow(1 - t, 3);
  };

  // Cargar imagen actual (sin cambios)
  useEffect(() => {
    const img = new Image();
    img.src = sectionImages[section];
    img.onload = () => setImage(img);
    img.onerror = (e) => console.error('Error loading image:', e);
  }, [section]);

  // Modificado: Iniciar desde el row 3 (índice 2)
  const calculateProgress = (index, startTime) => {
    const adjustedIndex = (index + 18) % NUM_STRIPS; // Ajuste para empezar desde el índice 2
    const elapsed = performance.now() - startTime - (adjustedIndex * STRIP_DELAY);
    const rawProgress = Math.min(elapsed / ANIMATION_DURATION, 1);
    return easeOutCubic(rawProgress);
  };

  // Animación modificada
  const animate = (startTime) => {
    const ctx = canvasRef.current?.getContext('2d');
    if (!ctx) return;

    stripsProgress.current = stripsProgress.current.map((_, index) => {
      return 1 - calculateProgress(index, startTime);
    });

    drawImage(ctx);

    if (performance.now() - startTime < (NUM_STRIPS * STRIP_DELAY) + ANIMATION_DURATION) {
      animationRef.current = requestAnimationFrame(() => animate(startTime));
    } else {
      isAnimating.current = false;
      stripsProgress.current = Array(NUM_STRIPS).fill(1);
      drawImage(ctx);
    }
  };

  // drawImage y demás funciones se mantienen igual
  // ... (resto del código sin cambios)

  return (
    <div 
      className="home__carrousel" 
      onClick={startAnimation}
      style={{ 
        position: 'relative',
        cursor: 'pointer',
        background: 'transparent !important',
        mixBlendMode: 'multiply'
      }}
    >
      <canvas 
        ref={canvasRef}
        style={{ 
          background: 'transparent',
          imageRendering: 'auto'
        }}
      />
    </div>
  );
};

export default HomeCarousel;
