import React, { useState, useEffect } from 'react';
import GrainOverlay from './GrainOverlay';
import '../assets/styles/App.css'; // Archivo de estilos específico

const Preloader = () => {
  const [closing, setClosing] = useState(false);
  const [loading, setLoading] = useState(true); // Nuevo estado para controlar la carga

  useEffect(() => {
    // Esperar 4 segundos antes de hacer el blur y cambiar la opacidad
    const timeout = setTimeout(() => {
      setClosing(true); // Cambia el estado para aplicar la animación de salida después de 4 segundos
      setLoading(false); // Cambia el estado a false una vez se haya cargado
    }, 4000); // 4 segundos

    // Establece el comportamiento al cerrar o recargar la página
    const handleBeforeUnload = () => {
      setClosing(true); // Cambia el estado si la página se va a cerrar
    };

    // Detecta cuando la pestaña se va a cerrar o recargar
    window.addEventListener('beforeunload', handleBeforeUnload);

    // Limpiar el timeout cuando el componente se desmonta
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
      clearTimeout(timeout);
    };
  }, []);

  return (
    <>
      <div className={`Preloader__Content ${closing ? 'closing' : ''}`} style={{ backgroundColor: loading ? 'orange' : 'transparent' }}>
        <GrainOverlay />
        <div className="Preloader__Container">
          <div className="Preloader__Text">
            STANDBY..
            <span className="blinking-dot">.</span>
          </div>
        </div>
      </div>
    </>
  );
};

export default Preloader;
