import React from 'react';
import '../assets/styles/App.css';
import { Player } from '@lottiefiles/react-lottie-player';
import '../assets/styles/Nav.css';
import atom from '../assets/imgs/atom.json';
import profile__pic from '../assets/imgs/prof__pic.jpeg';

const Home = () => {
  return (
    <div>
      <div className='left__side__content'>
        <div className='banner__section'>
          <div className='banner__profile'>
            <div className='profile__content'>
              {/* Image container on top */}
              <div className='profile__image-container'>
                <img 
                  src={profile__pic} 
                  alt='Profile' 
                  className='profile__image' 
                />
              </div>

              {/* Text content below image */}
              <div className='profile__info'>
                <h1 className='profile__title'>
                  Developer &<br />
                  Cyber-Sec
                </h1>
                <p className='profile__description'>
                  I'm a developer and cyber-sec enthusiast. I love to learn new things and explore new technologies.
                </p>
                <span className='profile__icons'>Icons</span>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className='right__side__content'>
        <div className='home__tittle'>
          hi im <span className='home__name'>veqqar</span>
          <div className='home__subtittle'>
            i love programming and design alse i love to learn new things
          </div>
        </div>
      </div>
      <button className="home__icon">
        <Player
          autoplay
          loop
          src={atom}
          className='atom'
        />
      </button>
    </div>

  );
};

export default Home;
