import { useEffect, useState } from 'react';
import '../assets/styles/App.css';

const AdditionalStuff = () => {
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isHoveringLink, setIsHoveringLink] = useState(false);

  useEffect(() => {
    // Update custom cursor position
    const handleMouseMove = (e) => {
      setCursorPosition({ x: e.clientX, y: e.clientY });
    };

    // Handle hover over links
    const handleHover = (e) => {
      if (e.target.tagName === 'A' || e.target.tagName === 'BUTTON' || e.target.classList.contains('home-icon') || e.target.classList.contains('onClick__Section__link')) {
        setIsHoveringLink(true);
      } else {
        setIsHoveringLink(false);
      }
    };

    // Prevent right-click context menu
    const handleContextMenu = (e) => {
      e.preventDefault();
      document.body.style.cursor = 'not-allowed';

      const resetCursor = () => {
        document.body.style.cursor = '';
      };

      document.addEventListener('click', resetCursor);
      setTimeout(resetCursor, 1000);
    };

    // Attach event listeners
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseover', handleHover);
    document.addEventListener('contextmenu', handleContextMenu);

    // Cleanup event listeners
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseover', handleHover);
      document.removeEventListener('contextmenu', handleContextMenu);
    };
  }, []);

  return (
    <div
      className={`custom-cursor ${isHoveringLink ? 'arrow' : ''}`}
      style={{
        left: `${cursorPosition.x}px`,
        top: `${cursorPosition.y}px`,
      }}
    />
  );
};

export default AdditionalStuff;
