import React, { createContext, useState, useEffect, useRef } from 'react';

// Creamos el contexto
export const ThemeContext = createContext();

// Proveedor del contexto
export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState('light'); // Tema por defecto
  const appRef = useRef(null);

  // Configurar la referencia del elemento app una vez
  useEffect(() => {
    appRef.current = document.querySelector('.app');
  }, []);

  // Cada vez que cambia el tema, actualizamos la clase en el contenedor raíz
  useEffect(() => {
    if (appRef.current) {
      appRef.current.classList.remove('dark', 'light');
      appRef.current.classList.add(theme);
    }
  }, [theme]);

  // Función para alternar el tema
  const toggleTheme = () => {
    setTheme(prev => (prev === 'dark' ? 'light' : 'dark'));
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};
