import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import NotFoundPage from "./components/NotFoundPage";
import Navbar from './components/Header';
import GrainOverlay from './components/GrainOverlay';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [loading, setLoading] = useState(true);
  const [showWelcome, setShowWelcome] = useState(true);

  useEffect(() => {
    const videoElement = document.querySelector('video');
    if (videoElement) {
      videoElement.onloadeddata = () => {
        setLoading(false);
        setShowWelcome(true);
      };

      const handleVideoPlay = () => {
        if (videoElement.paused) {
          videoElement.play();
        }
      };

      document.addEventListener('touchstart', handleVideoPlay);

      return () => {
        document.removeEventListener('touchstart', handleVideoPlay);
      };
    }
  }, []);

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home loading={loading} showWelcome={showWelcome} />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      default:
        return <NotFoundPage />;
    }
  }

  return (
    <>

      <Navbar setCurrentPage={setCurrentPage} />
      <div className={`home ${loading ? 'loading' : 'loaded'}`}>
        <video className='videoTag' autoPlay loop muted playsInline>
          <source src={background} type='video/mp4' />
        </video>
        {renderPage()}
      </div>
      <GrainOverlay />
    </>
  );
}

export default App;
