// HomeCarousel.jsx (Versión Corregida)
import React from 'react';

const HomeCarousel = ({ section }) => {
  const sectionNumber = section + 1;

  // Nueva forma de importar las imágenes
  const importAll = (r) => r.keys().map(r);
  const images = importAll(require.context(
    '../assets/imgs', 
    true, 
    /home_image_section\d+\/section\d+_image\d+\.jpg$/
  )).filter(path => path.includes(`home_image_section${sectionNumber}`));

  return (
    <div className="home__carrousel__img__rows">
      {images.map((img, index) => (
        <div
          key={index}
          className="image-part-container"
          style={{
            width: `${100 / images.length}%`,
            height: '100%'
          }}
        >
          <img
            src={img}
            alt={`Sección ${sectionNumber} - ${index + 1}`}
            className="images-rows"
          />
        </div>
      ))}
    </div>
  );
};

export default HomeCarousel;
