import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion'; // Importamos el motion de framer-motion
import '../assets/styles/App.css';
import profile from '../assets/imgs/viper.jpg';
import SectionFooter from './SectionFooter';

const Home = () => {
  const [currentSection, setCurrentSection] = useState(1);
  const [scrollDirection, setScrollDirection] = useState(null);
  const [scrollOrigin, setScrollOrigin] = useState(null);
  const [scrollDestination, setScrollDestination] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);

  // New function to handle the alerts
  const handleSectionChangeAlert = (origin, destination) => {
    const direction = origin < destination ? 'down' : 'up';
    setScrollOrigin(origin); // Guardamos el origen
    setScrollDestination(destination); // Guardamos el destino
    setScrollDirection(direction); // Guardamos la direcciÃ³n
  };

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    document.body.style.touchAction = 'none';

    const handleScroll = (e) => {
      if (isAnimating) return; // No hacemos nada si ya estamos animando

      if (e.deltaY > 0 && currentSection < 3) {
        setIsAnimating(true);
        setCurrentSection((prev) => {
          const newSection = prev + 1;
          handleSectionChangeAlert(prev, newSection); // Call the new function
          return newSection;
        });
      } else if (e.deltaY < 0 && currentSection > 1) {
        setIsAnimating(true);
        setCurrentSection((prev) => {
          const newSection = prev - 1;
          handleSectionChangeAlert(prev, newSection); // Call the new function
          return newSection;
        });
      }
    };

    window.addEventListener('wheel', handleScroll);

    return () => {
      document.body.style.overflow = 'auto';
      document.body.style.touchAction = 'auto';
      window.removeEventListener('wheel', handleScroll);
    };
  }, [currentSection, isAnimating]);

  const handleFooterNavigation = (newSection) => {
    if (newSection !== currentSection) {
      setIsAnimating(true);
      handleSectionChangeAlert(currentSection, newSection); // Call the new function
      setCurrentSection(newSection);
    }
  };

  const renderSection = () => {
    switch (currentSection) {
      case 1:
        return (
          <motion.div
            key={1}
            className="home__section1"
            initial={{ x: scrollDirection === 'down' ? '100%' : '-100%' }}
            animate={{ x: 0 }}
            exit={{ x: scrollDirection === 'down' ? '-100%' : '100%' }}
            transition={{ type: 'spring', stiffness: 300 }}
            onAnimationComplete={() => setIsAnimating(false)} // Reset animation flag
          >
            <div className="section">
              <div className="profile">
                <img src={profile} alt="Profile" />
              </div>
              <div className="home__section1__message">
                <p><b>hey, im dave</b></p>
                <p>
                  i'm a developer based in <b>Barcelona</b>. I create things like&nbsp;
                  <span style={{ color: '#e96f7c' }}>sites & apps</span> and delve into&nbsp;
                  <span style={{ color: '#e96f7c' }}>hacking & malware</span> and whatever else.
                  The page is still under maintenance, so feel free to look around if you want..
                </p>
              </div>
            </div>
          </motion.div>
        );

      case 2:
        return (
          <motion.div
            key={2}
            className="home__section2"
            initial={{ x: scrollDirection === 'down' ? '100%' : '-100%' }}
            animate={{ x: 0 }}
            exit={{ x: scrollDirection === 'down' ? '-100%' : '100%' }}
            transition={{ type: 'spring', stiffness: 300 }}
            onAnimationComplete={() => setIsAnimating(false)} // Reset animation flag
          >
            <h1 style={{ color: 'white' }}>Special Abilities</h1>
            <ul>
              <li>Desarrollo Web: CreaciÃ³n de sitios y aplicaciones web dinÃ¡micas y responsivas.</li>
              <li>ProgramaciÃ³n de Software: Desarrollo de aplicaciones personalizadas para diversas necesidades.</li>
              <li>Ciberseguridad: Especializado en malware pentesting y prÃ¡cticas seguras de red.</li>
              <li>Redes InformÃ¡ticas: ConfiguraciÃ³n y optimizaciÃ³n de infraestructuras de red.</li>
            </ul>
            <p>
              <b>PD:</b> if you want to know more about my skills, you can contact me through my social networks.
            </p>
          </motion.div>
        );

      case 3:
        return (
          <motion.div
            key={3}
            className="home__section3"
            initial={{ x: scrollDirection === 'down' ? '100%' : '-100%' }}
            animate={{ x: 0 }}
            exit={{ x: scrollDirection === 'down' ? '-100%' : '100%' }}
            transition={{ type: 'spring', stiffness: 300 }}
            onAnimationComplete={() => setIsAnimating(false)} // Reset animation flag
          >
            <h1 style={{ color: 'white' }}>Some of my stuff and side projects:</h1>
            <ul>
              <li>Proyect 1: <a href="">Link</a></li>
              <li>Proyect 2: <a href="">Link</a></li>
              <li>Proyect 3: <a href="">Link</a></li>
              <li>Proyect 4: <a href="">Link</a></li>
            </ul>
          </motion.div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="content">
      <div>
        {renderSection()}
        <SectionFooter currentSection={currentSection} setCurrentSection={setCurrentSection} />
      </div>
    </div>
  );
};

export default Home;
