import React from 'react';
import '../assets/styles/App.css';

const SectionFooter = ({ currentSection, setCurrentSection }) => {
  return (
    <div className='section__bar'>
      <ul className='section__links'>
        <li className='onClick__Section__link' onClick={() => setCurrentSection(1)}>
          <div className={currentSection === 1 ? 'dot__focused' : 'dot'}></div>
        </li>
        <li className='onClick__Section__link' onClick={() => setCurrentSection(2)}>
          <div className={currentSection === 2 ? 'dot__focused' : 'dot'}></div>
        </li>
        <li className='onClick__Section__link' onClick={() => setCurrentSection(3)}>
          <div className={currentSection === 3 ? 'dot__focused' : 'dot'}></div>
        </li>
      </ul>
    </div>
  );
};

export default SectionFooter;
