import React, { useState } from 'react';
import '../assets/styles/App.css';
import '../assets/styles/Nav.css';
import carouselImage from '../assets/imgs/carrousel__img__1.jpg';

const HomePage = () => {
  const [hoveredColumn, setHoveredColumn] = useState(null);
  const [isFadeActive, setIsFadeActive] = useState(false);
  const totalColumns = 20;

  const ANIMATION_DURATION = 600;
  const DELAY_INCREMENT = 50;

  const handleCarouselClick = () => {
    setIsFadeActive(true);
    setTimeout(
      () => setIsFadeActive(false),
      totalColumns * DELAY_INCREMENT + ANIMATION_DURATION
    );
  };

  return (
    <div>
      <div className="left-panel">
        <div className="banner-description">
          <div className="banner-description__section"></div>
          <div className="banner-description__section">
            My name is David Gomez Pilipenko aka //veqqar im a software developer and cybersecurity specialist. Based in Barcelona (ES).
          </div>
        </div>

        <div className="carousel-slider">
          <button className="carousel-slider__prev" onClick={''}>
            PREV
          </button>
          <div className="carousel-slider__nav"> ■   ■   ■   ■   ■</div>
          <button className="carousel-slider__next" onClick={''}>
            NEXT
          </button>
        </div>

        <div className="home__subtitle-intro">Me</div>
        <div className="home__title">About myself</div>

        <div className="link-container">
          <button className="link-button">
            Visit <i className="fa-solid fa-square-arrow-up-right"></i>
          </button>
        </div>
      </div>

      <div className="right-panel">
        <div className="home-title">
          <div className="header-section">
            hi im <span className="home-name">veqqar</span>
          </div>

          <div className="carousel" style={{ position: 'relative' }}>
            <img
              src={carouselImage}
              alt="Carousel"
              className="carousel__image"
            />

            {/* Contenedor de columnas animadas */}
            <div className="carousel__overlay" onClick={handleCarouselClick}>
              {Array.from({ length: totalColumns }, (_, index) => {
                let blurValue = 0;
                let saturationValue = 1;

                if (
                  hoveredColumn !== null &&
                  Math.abs(index - hoveredColumn) <= 3
                ) {
                  const distance = Math.abs(index - hoveredColumn);
                  blurValue = 7.5 - distance * 1.5;
                  saturationValue = 1 + (3 - distance) * 0.25;
                }

                return (
                  <div
                    key={index}
                    className="carousel__overlay-column"
                    onMouseEnter={() => setHoveredColumn(index)}
                    onMouseLeave={() => setHoveredColumn(null)}
                    style={{
                      '--blur': `${blurValue}px`,
                      '--saturate': saturationValue,
                      '--delay': `${index * DELAY_INCREMENT}ms`,
                    }}
                  >
                    <div
                      className="carousel__fill-bar"
                      style={
                        isFadeActive
                          ? {
                              animation: `expandWidth ${ANIMATION_DURATION}ms cubic-bezier(0.4, 0, 0.2, 1) forwards`,
                              animationDelay: `${index * DELAY_INCREMENT}ms`,
                            }
                          : {}
                      }
                    />
                  </div>
                );
              })}
            </div>
          </div>

          <div className="home__subtitle">
            <div className="home__subtitle-sections">
              <div className="home__subtitle-section"></div>
              <div className="home__subtitle-section">
                <div className="home__subtitle-section__title">■ Experience</div>
                <div className="home__subtitle-section__content" style={{ fontSize: '1rem' }}>
                  // T+ 4y : 3m : 2w
                </div>
              </div>
              <div className="home__subtitle-section">
                <div className="home__subtitle-section__title">■ Tools</div>
                <div className="home__subtitle-section__content">
                  // C / Python / JS <br />/ React / SQL / NoSQL <br />/ Git / Docker / Kali <br />/ Metasploit / Burp <br />/ Nmap //
                </div>
              </div>
              <div className="home__subtitle-section">
                <div className="home__subtitle-section__title">■ Skills</div>
                <div className="home__subtitle-section__content">
                  // Programming <br /> / Cyber-Security <br /> / Web-Dev / UI/UX //
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
