import React from 'react';

// Importa manualmente las imágenes de cada sección
const section1Images = Array.from({ length: 20 }, (_, i) => 
  require(`../assets/imgs/home_image_section1/section1_image${i + 1}.jpg`).default
);

const section2Images = Array.from({ length: 20 }, (_, i) => 
  require(`../assets/imgs/home_image_section2/section2_image${i + 1}.jpg`).default
);

const section3Images = Array.from({ length: 20 }, (_, i) => 
  require(`../assets/imgs/home_image_section3/section3_image${i + 1}.jpg`).default
);

const HomeCarousel = ({ section, fadeActive, animationDuration, delayIncrement }) => {
  const images = [
    section1Images,
    section2Images,
    section3Images
  ][section];

  return (
    <div className="home__carrousel__img__rows">
      {images.map((img, index) => (
        <div
          key={index}
          className="image-part-container"
          style={{
            animation: fadeActive 
              ? `expandWidth ${animationDuration}ms cubic-bezier(0.4, 0, 0.2, 1) forwards`
              : 'none',
            animationDelay: fadeActive ? `${index * delayIncrement}ms` : '0ms'
          }}
        >
          <img
            src={img}
            alt={`Sección ${section + 1} - Parte ${index + 1}`}
            className="image-part"
          />
        </div>
      ))}
    </div>
  );
};

export default HomeCarousel;
